import React from 'react';

const team = [
  { name: 'Sonu Jha', role: 'Project Lead & Backend Developer', email: 'sonujha@example.com' },
  { name: 'Amit Kumar', role: 'Frontend Developer', email: 'amitkumar@example.com' },
  { name: 'Priya Singh', role: 'AI/ML Specialist', email: 'priyasingh@example.com' },
  { name: 'Ravi Patel', role: 'UI/UX Designer', email: 'ravipatel@example.com' },
];

function AboutPage() {
  return (
    <section className="bg-darkBg text-text">
      <div className="max-w-3xl mx-auto py-section px-4 mt-16">
        <h2 className="h1 mb-2">About AgriGuru</h2>
        <div className="divider" />
        <p className="text-lg text-sage mb-8 font-sans">
          AgriGuru is a passion project developed by a dedicated team of students and professionals, aiming to empower farmers with the latest AI technology. Our mission is to make advanced agricultural knowledge accessible, actionable, and easy to use for everyone.
        </p>
        <h3 className="h2 mb-4">Meet the Team</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {team.map(member => (
            <div key={member.email} className="bg-darkBg rounded-2xl p-6 shadow-card border border-border flex flex-col gap-2 hover:border-emerald transition-all duration-200">
              <h4 className="text-xl font-heading font-semibold text-emerald mb-1">{member.name}</h4>
              <p className="text-sage mb-1 font-sans">{member.role}</p>
              <a href={`mailto:${member.email}`} className="text-emerald hover:underline text-sm font-sans">{member.email}</a>
            </div>
          ))}
        </div>
        <div className="divider" />
        <div className="mt-8 text-sage text-sm text-center">
          &copy; {new Date().getFullYear()} AgriGuru Team. All rights reserved.
        </div>
      </div>
    </section>
  );
}

export default AboutPage;
