import React, { useState } from 'react';
import { getWeather } from '../api/api';

function WeatherCard() {
  const [location, setLocation] = useState('');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFetch = async () => {
    setLoading(true); setError(null); setWeather(null);
    try {
      const res = await getWeather({ location, user_id: 'guest' });
      setWeather(res.data);
    } catch (err) {
      setError('Failed to fetch weather');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-xl font-semibold mb-2">Weather Forecast</h2>
      <div className="flex gap-2 mb-2">
        <input className="border p-2" placeholder="Enter Location" value={location} onChange={e => setLocation(e.target.value)} />
        <button className="bg-blue-600 text-white p-2 rounded" onClick={handleFetch}>Fetch Weather</button>
      </div>
      {loading && <div className="text-blue-600">Loading...</div>}
      {error && <div className="text-red-600">{error}</div>}
      {weather && Array.isArray(weather) ? (
        <div className="p-2 bg-blue-50 border rounded">
          {weather.map((w, i) => (
            <div key={i} className="mb-1">
              <b>{w.datetime}:</b> Temp: {w.temp}&deg;C, Humidity: {w.humidity}%, Rain: {w.rain}mm, Wind: {w.wind} m/s
            </div>
          ))}
        </div>
      ) : weather && weather.forecast && (
        <div className="p-2 bg-blue-50 border rounded">{weather.forecast}</div>
      )}
    </div>
  );
}
export default WeatherCard;
