import React, { useEffect, useState } from 'react';
import { getTips } from '../api/api';

function TipsSection() {
  const [tips, setTips] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [query, setQuery] = useState("");
  const [answer, setAnswer] = useState(null);
  const [answerLoading, setAnswerLoading] = useState(false);

  useEffect(() => {
    setLoading(true); setError(null);
    getTips().then(res => setTips(res.data)).catch(() => setError('Failed to fetch tips')).finally(() => setLoading(false));
  }, []);

  const handleAsk = async (e) => {
    e.preventDefault();
    setAnswer(null); setAnswerLoading(true);
    // Simulate AI answer (replace with real API call if available)
    setTimeout(() => {
      setAnswer(`This is a helpful answer about: "${query}". (Replace with real AI response)`);
      setAnswerLoading(false);
    }, 1200);
  };

  return (
    <div className="relative bg-white/80 p-6 rounded-2xl shadow-lg border border-green-200 overflow-hidden">
      <img src="https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=800&q=80" alt="leaves" className="absolute inset-0 w-full h-full object-cover opacity-25 pointer-events-none" />
      <div className="absolute inset-0 bg-gradient-to-tr from-green-200/70 to-transparent pointer-events-none" />
      <h2 className="text-2xl font-bold text-green-800 mb-3 flex items-center gap-2">
        <span role="img" aria-label="tips">ðŸŒ±</span> Organic Farming Tips
      </h2>
      {loading && <div className="text-blue-600">Loading...</div>}
      {error && <div className="text-red-600">{error}</div>}
      <ul className="list-disc ml-6 mb-6">
        {tips.map((tip, i) => (
          <li key={i} className="mb-1"><b>{tip.category} ({tip.stage}):</b> {tip.tip}</li>
        ))}
      </ul>
      <form onSubmit={handleAsk} className="flex flex-col sm:flex-row gap-2 items-stretch mb-2">
        <input
          className="border border-green-300 rounded-lg p-2 flex-1 focus:ring-2 focus:ring-green-400 outline-none transition"
          placeholder="Ask a question about organic farming, crops, or soil..."
          value={query}
          onChange={e => setQuery(e.target.value)}
          required
        />
        <button
          className="bg-gradient-to-r from-green-600 to-emerald-500 text-white font-semibold p-2 rounded-lg shadow hover:from-green-700 hover:to-emerald-600 transition-all duration-200"
          type="submit"
          disabled={answerLoading || !query.trim()}
        >{answerLoading ? 'Thinking...' : 'Ask'}</button>
      </form>
      {answer && (
        <div className="mt-2 p-3 bg-green-50 border border-green-200 rounded-lg animate-fade-in text-green-900">
          <b>AI Answer:</b> {answer}
        </div>
      )}
    </div>
  );
}
export default TipsSection;
