import React, { useEffect, useState } from 'react';
import { getTips } from '../api/api';

function TipsSection() {
  const [tips, setTips] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true); setError(null);
    getTips().then(res => setTips(res.data)).catch(() => setError('Failed to fetch tips')).finally(() => setLoading(false));
  }, []);

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-xl font-semibold mb-2">Farming Tips</h2>
      {loading && <div className="text-blue-600">Loading...</div>}
      {error && <div className="text-red-600">{error}</div>}
      <ul className="list-disc ml-6">
        {tips.map((tip, i) => (
          <li key={i}><b>{tip.category} ({tip.stage}):</b> {tip.tip}</li>
        ))}
      </ul>
    </div>
  );
}
export default TipsSection;
