import React from 'react';

function Layout({ children }) {
  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-green-50 via-green-100 to-emerald-200 font-sans">
      <header className="bg-gradient-to-r from-green-700 via-lime-600 to-emerald-600 shadow-lg py-6 px-4 flex items-center justify-between">
        <div className="flex items-center gap-3">
          <img src="/favicon.ico" alt="AgriGuru Logo" className="w-10 h-10 rounded-full shadow-md" />
          <span className="text-3xl font-extrabold text-white tracking-tight drop-shadow-lg">AgriGuru</span>
        </div>
        <span className="text-lg text-green-100 italic hidden md:block">Empowering Farmers with AI</span>
      </header>
      <main className="flex-1 w-full max-w-5xl mx-auto px-2 py-6 md:py-10 flex flex-col gap-8">
        {children}
      </main>
      <footer className="bg-green-900 text-green-100 text-center py-3 shadow-inner text-sm">
        &copy; {new Date().getFullYear()} AgriGuru. All rights reserved.
      </footer>
    </div>
  );
}

export default Layout;
