import React, { useState } from 'react';
import { getWeather } from '../api/api';

function WeatherCard() {
  const [location, setLocation] = useState('');
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFetch = async () => {
    setLoading(true); setError(null); setWeather(null);
    try {
      const res = await getWeather({ location, user_id: 'guest' });
      setWeather(res.data);
    } catch (err) {
      setError('Failed to fetch weather');
    } finally {
      setLoading(false);
    }
  };

  return (
  <div className="relative bg-white/80 p-6 rounded-2xl shadow-lg border border-green-200 overflow-hidden">
    <img src="https://images.unsplash.com/photo-1502086223501-7ea6ecd79368?auto=format&fit=crop&w=800&q=80" alt="sky" className="absolute inset-0 w-full h-full object-cover opacity-30 pointer-events-none" />
    <div className="absolute inset-0 bg-gradient-to-tr from-sky-200/70 to-transparent pointer-events-none" />
    <div className="flex items-center gap-3 mb-3">
      <span className="inline-flex items-center justify-center bg-blue-600 text-white rounded-full w-10 h-10 shadow-md">
        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
          <path strokeLinecap="round" strokeLinejoin="round" d="M12 3v1.5m0 15V21m8.485-8.485l-1.06 1.06M4.515 4.515l1.06 1.06M21 12h-1.5M3 12H1.5m16.97 5.485l-1.06-1.06M6.03 17.97l1.06-1.06" />
        </svg>
      </span>
      <h2 className="text-2xl font-bold text-blue-800 tracking-tight">Weather Forecast</h2>
    </div>
    <div className="flex flex-col sm:flex-row gap-2 mb-4">
      <input className="border border-blue-300 rounded-lg p-2 flex-1 focus:ring-2 focus:ring-blue-400 outline-none transition" placeholder="Enter Location" value={location} onChange={e => setLocation(e.target.value)} />
      <button className="bg-gradient-to-r from-blue-600 to-emerald-500 text-white font-semibold p-2 rounded-lg shadow hover:from-blue-700 hover:to-emerald-600 transition-all duration-200" onClick={handleFetch} disabled={loading}>{loading ? 'Loading...' : 'Fetch Weather'}</button>
    </div>
    {error && <div className="text-red-600 bg-red-50 rounded p-2 border border-red-200 animate-pulse mb-2">{error}</div>}
    {weather && Array.isArray(weather) && (
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        {weather.map((w, i) => (
          <div key={i} className="bg-white/90 border border-blue-100 rounded-xl p-4 shadow flex flex-col gap-2 items-center">
            <div className="font-semibold text-blue-800 mb-1">{w.datetime}</div>
            <div className="flex gap-4 items-center">
              <div className="flex flex-col items-center">
                <span className="text-xl">üå°Ô∏è</span>
                <span className="text-blue-900">{w.temp}&deg;C</span>
                <span className="text-xs text-blue-600">Temp</span>
              </div>
              <div className="flex flex-col items-center">
                <span className="text-xl">üíß</span>
                <span className="text-blue-900">{w.humidity}%</span>
                <span className="text-xs text-blue-600">Humidity</span>
              </div>
              <div className="flex flex-col items-center">
                <span className="text-xl">üåßÔ∏è</span>
                <span className="text-blue-900">{w.rain}mm</span>
                <span className="text-xs text-blue-600">Rain</span>
              </div>
              <div className="flex flex-col items-center">
                <span className="text-xl">üí®</span>
                <span className="text-blue-900">{w.wind} m/s</span>
                <span className="text-xs text-blue-600">Wind</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    )}
    {weather && weather.forecast && (
      <div className="p-4 bg-blue-50 border border-blue-200 rounded-lg text-blue-900 mt-2">{weather.forecast}</div>
    )}
  </div>
);
}
export default WeatherCard;
