import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './components/Home';
import CropRecommendationPage from './components/CropRecommendationPage';
import WeatherPage from './components/WeatherPage';
import TipsPage from './components/TipsPage';
import SchemesPage from './components/SchemesPage';
import DashboardPage from './components/DashboardPage';
import ChatbotPage from './components/ChatbotPage';
import SatelliteInsightsPage from './components/SatelliteInsightsPage';

// Add Google Font for branding
const fontLink = document.createElement('link');
fontLink.href = 'https://fonts.googleapis.com/css2?family=Pacifico&display=swap';
fontLink.rel = 'stylesheet';
document.head.appendChild(fontLink);

function App() {
  return (
    <Router>
      <div className="flex flex-col min-h-screen bg-gradient-to-br from-green-50 to-emerald-100">
        <Navbar />
        <main className="flex-1 pt-20 pb-8 px-2 md:px-0">
          <Routes>
            <Route path="/satellite" element={
              <section className="max-w-3xl mx-auto mt-8 mb-8 border-4 border-blue-500 bg-gradient-to-br from-blue-100 to-green-100 rounded-3xl shadow-2xl p-6 animate-pulse-slow">
                <SatelliteInsightsPage />
              </section>
            } />
            <Route path="/" element={
              <section className="max-w-3xl mx-auto mt-8 mb-8 border-2 border-green-300 rounded-2xl shadow-lg bg-white/90 p-4">
                <Home />
              </section>
            } />
            <Route path="/crop-recommendation" element={
              <section className="max-w-3xl mx-auto mt-8 mb-8 border-2 border-emerald-400 rounded-2xl shadow-lg bg-white/90 p-4">
                <CropRecommendationPage />
              </section>
            } />
            <Route path="/weather" element={
              <section className="max-w-3xl mx-auto mt-8 mb-8 border-2 border-blue-300 rounded-2xl shadow-lg bg-white/90 p-4">
                <WeatherPage />
              </section>
            } />
            <Route path="/tips" element={
              <section className="max-w-3xl mx-auto mt-8 mb-8 border-2 border-lime-400 rounded-2xl shadow-lg bg-white/90 p-4">
                <TipsPage />
              </section>
            } />
            <Route path="/schemes" element={
              <section className="max-w-3xl mx-auto mt-8 mb-8 border-2 border-yellow-400 rounded-2xl shadow-lg bg-white/90 p-4">
                <SchemesPage />
              </section>
            } />
            <Route path="/dashboard" element={
              <section className="max-w-3xl mx-auto mt-8 mb-8 border-2 border-purple-400 rounded-2xl shadow-lg bg-white/90 p-4">
                <DashboardPage />
              </section>
            } />
            <Route path="/chatbot" element={
              <section className="max-w-3xl mx-auto mt-8 mb-8 border-2 border-pink-400 rounded-2xl shadow-lg bg-white/90 p-4">
                <ChatbotPage />
              </section>
            } />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
