import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';

const menu = [
  { name: 'Home', path: '/', icon: <span className="text-lg">üè†</span> },
  { name: 'Crop Recommendation', path: '/crop-recommendation', icon: <span className="text-lg">üå±</span> },
  { name: 'Weather', path: '/weather', icon: <span className="text-lg">‚õÖ</span> },
  { name: 'Organic Tips', path: '/tips', icon: <span className="text-lg">üçÉ</span> },
  { name: 'Government Schemes', path: '/schemes', icon: <span className="text-lg">üèõÔ∏è</span> },
  { name: 'Dashboard', path: '/dashboard', icon: <span className="text-lg">üìä</span> },
  { name: 'Chatbot', path: '/chatbot', icon: <span className="text-lg">ü§ñ</span> }
];

function Navbar() {
  const [open, setOpen] = useState(false);
  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-white/90 shadow-md backdrop-blur border-b border-green-200">
      <div className="max-w-6xl mx-auto px-4 flex items-center justify-between h-16">
        <Link to="/" className="flex items-center text-2xl font-bold text-green-700 gap-2">
          <span role="img" aria-label="logo">üåæ</span> AgriGuru
        </Link>
        <button className="md:hidden p-2" onClick={() => setOpen(!open)}>
          <svg className="w-7 h-7 text-green-700" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={open ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'} />
          </svg>
        </button>
        <div className={`md:flex gap-6 font-medium ${open ? 'block absolute bg-white left-0 right-0 top-16 shadow-lg p-4' : 'hidden md:block'}`}>
          {menu.map(item => (
  <NavLink
    key={item.path}
    to={item.path}
    onClick={() => setOpen(false)}
    className={({ isActive }) =>
      (isActive
        ? 'text-green-700 border-b-2 border-green-600 pb-1'
        : 'hover:text-green-600 transition') +
      ' flex items-center gap-1 px-2 py-1 rounded-lg'
    }
  >
    <span className="mr-1">{item.icon}</span>
    <span>{item.name}</span>
  </NavLink>
))}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
