import React, { useState } from 'react';
import { recommendCrop } from '../api/api';

const soilTypes = [
  { name: 'Sandy', img: 'https://upload.wikimedia.org/wikipedia/commons/4/4d/Sandy_soil.jpg' },
  { name: 'Clay', img: 'https://upload.wikimedia.org/wikipedia/commons/8/8f/Clay_soil.jpg' },
  { name: 'Silty', img: 'https://upload.wikimedia.org/wikipedia/commons/7/7c/Silt_soil.jpg' },
  { name: 'Peaty', img: 'https://upload.wikimedia.org/wikipedia/commons/2/2e/Peaty_soil.jpg' },
  { name: 'Chalky', img: 'https://upload.wikimedia.org/wikipedia/commons/8/8e/Chalky_soil.jpg' },
  { name: 'Loamy', img: 'https://upload.wikimedia.org/wikipedia/commons/0/0c/Loamy_soil.jpg' }
];
const cropImages = {
  Wheat: 'https://images.unsplash.com/photo-1504593811423-6dd665756598?auto=format&fit=crop&w=400&q=80',
  Rice: 'https://images.unsplash.com/photo-1501004318641-b39e6451bec6?auto=format&fit=crop&w=400&q=80',
  Maize: 'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80',
  Sugarcane: 'https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=crop&w=400&q=80',
  Cotton: 'https://images.unsplash.com/photo-1502741338009-cac2772e18bc?auto=format&fit=crop&w=400&q=80',
  Barley: 'https://images.unsplash.com/photo-1465101046530-73398c7f28ca?auto=format&fit=crop&w=400&q=80',
  Soybean: 'https://images.unsplash.com/photo-1506784983877-45594efa4cbe?auto=format&fit=crop&w=400&q=80'
};
const seasons = ['Kharif', 'Rabi', 'Zaid'];

function CropRecommendationForm() {
  const [form, setForm] = useState({ soil: '', season: '', location: '' });
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); setError(null); setResult(null);
    try {
      const res = await recommendCrop({ ...form, user_id: 'guest' });
      setResult(res.data);
    } catch (err) {
      setError('Failed to get recommendation');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gradient-to-br from-green-100 to-emerald-100 rounded-2xl shadow-lg p-6 transition-transform hover:scale-[1.02] border border-green-200">
      <div className="flex items-center gap-3 mb-3">
        <span className="inline-flex items-center justify-center bg-green-600 text-white rounded-full w-10 h-10 shadow-md">
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
            <path strokeLinecap="round" strokeLinejoin="round" d="M12 2.25v19.5m9.75-9.75H2.25" />
          </svg>
        </span>
        <h2 className="text-2xl font-bold text-green-800 tracking-tight">Crop Recommendation</h2>
      </div>
      <form onSubmit={handleSubmit} className="flex flex-col gap-3">
  <div>
    <div className="font-semibold mb-2 text-green-700">Choose Soil Type:</div>
    <div className="grid grid-cols-2 sm:grid-cols-3 gap-3 mb-2">
      {soilTypes.map(type => (
        <button
          type="button"
          key={type.name}
          className={`group border-2 rounded-xl flex flex-col items-center p-2 transition shadow hover:shadow-lg focus:outline-none ${form.soil === type.name ? 'border-green-600 bg-green-100' : 'border-green-200 bg-white'}`}
          onClick={() => setForm({ ...form, soil: type.name })}
        >
          <img src={type.img} alt={type.name + ' soil'} className="w-16 h-16 object-cover rounded-lg mb-1 border border-green-300 group-hover:scale-105 transition" onError={e => { e.target.onerror = null; e.target.src = 'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80'; }} />
          <span className="text-green-900 font-medium">{type.name}</span>
        </button>
      ))}
    </div>
  </div>
  <select className="border border-green-300 rounded-lg p-2 focus:ring-2 focus:ring-green-400 outline-none transition" required value={form.season} onChange={e => setForm({ ...form, season: e.target.value })}>
    <option value="">Season</option>
    {seasons.map(season => <option key={season} value={season}>{season}</option>)}
  </select>
  <input className="border border-green-300 rounded-lg p-2 focus:ring-2 focus:ring-green-400 outline-none transition" required placeholder="Enter Location" value={form.location} onChange={e => setForm({ ...form, location: e.target.value })} />
  <button className="bg-gradient-to-r from-green-600 to-emerald-500 text-white font-semibold p-2 rounded-lg shadow hover:from-green-700 hover:to-emerald-600 transition-all duration-200" type="submit" disabled={loading}>{loading ? 'Loading...' : 'Get Recommendation'}</button>
</form>
      {error && <div className="mt-3 text-red-600 bg-red-50 rounded p-2 border border-red-200 animate-pulse">{error}</div>}
      {result && (
  <div className="mt-4 p-4 bg-green-50 border border-green-200 rounded-lg animate-fade-in">
    <div className="text-lg font-semibold text-green-800 mb-1"><b>Suggested Crops:</b></div>
    <div className="flex flex-wrap gap-4 mb-2">
      {Array.isArray(result.crops)
        ? result.crops.map((crop, i) => (
            <div key={i} className="flex flex-col items-center w-24">
              <img src={cropImages[crop] || 'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80'} alt={crop} className="w-16 h-16 object-cover rounded-lg border border-green-300 mb-1" onError={e => { e.target.onerror = null; e.target.src = 'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80'; }} />
              <span className="text-green-900 font-medium text-sm text-center">{crop}</span>
            </div>
          ))
        : <span>{result.crops}</span>
      }
    </div>
    <div className="text-green-900"><b>Reason:</b> {result.reason}</div>
  </div>
)}
    </div>
  );
}
export default CropRecommendationForm;
