import React, { useState, useEffect } from 'react';
import { getCropPrices, getPopularCrops } from '../api/api';

const CropPricesForm = () => {
  const [formData, setFormData] = useState({
    location: '',
    crop_name: ''
  });
  const [popularCrops, setPopularCrops] = useState([]);
  const [prices, setPrices] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [refreshKey, setRefreshKey] = useState(0);
  const [loadingQuote, setLoadingQuote] = useState('');
  const [quoteIndex, setQuoteIndex] = useState(0);

  const loadingQuotes = [
    "üåæ Fetching real-time prices from government mandis...",
    "üìä Analyzing market data from AGMARKNET...",
    "üèõÔ∏è Checking eNAM (National Agriculture Market) prices...",
    "üìà Gathering data from state agricultural boards...",
    "üîç Searching for the latest mandi rates...",
    "üíº Compiling comprehensive price information...",
    "üìã Verifying data from multiple government sources...",
    "üå± Processing crop-specific market insights..."
  ];

  useEffect(() => {
    fetchPopularCrops();
  }, []);

  useEffect(() => {
    if (loading) {
      const interval = setInterval(() => {
        setQuoteIndex((prev) => (prev + 1) % loadingQuotes.length);
      }, 3000);
      return () => clearInterval(interval);
    }
  }, [loading]);

  useEffect(() => {
    setLoadingQuote(loadingQuotes[quoteIndex]);
  }, [quoteIndex]);

  const fetchPopularCrops = async () => {
    try {
      const response = await getPopularCrops();
      console.log('Popular crops response:', response);
      if (response.data && response.data.success) {
        setPopularCrops(response.data.crops);
      } else {
        console.error('Invalid response format:', response);
        // Fallback to default crops if API fails
        setPopularCrops(['Rice', 'Wheat', 'Maize', 'Sugarcane', 'Cotton', 'Pulses', 'Oilseeds', 'Vegetables', 'Fruits', 'Tea', 'Coffee', 'Spices']);
      }
    } catch (err) {
      console.error('Error fetching popular crops:', err);
      // Fallback to default crops if API fails
      setPopularCrops(['Rice', 'Wheat', 'Maize', 'Sugarcane', 'Cotton', 'Pulses', 'Oilseeds', 'Vegetables', 'Fruits', 'Tea', 'Coffee', 'Spices']);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.location.trim() || !formData.crop_name.trim()) {
      setError('Please enter both location and crop name');
      return;
    }

    setLoading(true);
    setError('');
    setPrices(null);

    try {
      const response = await getCropPrices(formData);
      console.log('Crop prices response:', response);
      if (response.data && response.data.success) {
        setPrices(response.data.data);
      } else {
        setError(response.data?.message || 'Failed to fetch prices');
      }
    } catch (err) {
      console.error('Error fetching crop prices:', err);
      setError('Error connecting to server. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleRefresh = async () => {
    setLoading(true);
    setError('');
    setPrices(null);
    setRefreshKey(prev => prev + 1);
    try {
      const response = await getCropPrices(formData);
      if (response.data && response.data.success) {
        setPrices(response.data.data);
      } else {
        setError(response.data?.message || 'Failed to fetch prices');
      }
    } catch (err) {
      setError('Error connecting to server. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(price);
  };

  const getCropIcon = (cropName) => {
    const crop = cropName.toLowerCase();
    if (crop.includes('rice')) return 'üåæ';
    if (crop.includes('wheat')) return 'üåæ';
    if (crop.includes('maize')) return 'üåΩ';
    if (crop.includes('sugarcane')) return 'üéã';
    if (crop.includes('cotton')) return 'üß∂';
    if (crop.includes('pulses')) return 'ü´ò';
    if (crop.includes('vegetables')) return 'ü•¨';
    if (crop.includes('fruits')) return 'üçé';
    if (crop.includes('tea')) return 'üçµ';
    if (crop.includes('coffee')) return '‚òï';
    if (crop.includes('spices')) return 'üå∂Ô∏è';
    return 'üå±';
  };

  return (
    <div className="max-w-4xl mx-auto">
      {/* Search Form */}
      <div className="bg-white rounded-xl shadow-lg p-6 mb-8">
        <h2 className="text-2xl font-semibold text-gray-800 mb-6 text-center">
          Search Crop Prices
        </h2>
        
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid md:grid-cols-2 gap-6">
            <div>
              <label htmlFor="location" className="block text-sm font-medium text-gray-700 mb-2">
                Location (City/District)
              </label>
              <input
                type="text"
                id="location"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                placeholder="e.g., Bangalore, Karnataka"
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent transition-all duration-200"
                required
              />
            </div>
            
            <div>
              <label htmlFor="crop_name" className="block text-sm font-medium text-gray-700 mb-2">
                Crop Name
              </label>
              <select
                id="crop_name"
                name="crop_name"
                value={formData.crop_name}
                onChange={handleInputChange}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-transparent transition-all duration-200 text-gray-900"
                required
              >
                <option value="" className="text-gray-900">Select a crop</option>
                {popularCrops.map((crop, index) => (
                  <option key={index} value={crop} className="text-gray-900">
                    {crop}
                  </option>
                ))}
              </select>
            </div>
          </div>
          
          <div className="text-center">
            <button
              type="submit"
              disabled={loading}
              className="bg-gradient-to-r from-green-600 to-emerald-600 text-white px-8 py-3 rounded-lg font-semibold hover:from-green-700 hover:to-emerald-700 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed shadow-lg"
            >
              {loading ? (
                <span className="flex items-center justify-center">
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Fetching Real Data...
                </span>
              ) : (
                'Get Real-Time Prices'
              )}
            </button>
          </div>
        </form>
        
        {loading && (
          <div className="mt-8 text-center">
            <div className="bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl p-8 border border-blue-200">
              <div className="flex items-center justify-center mb-4">
                <div className="relative">
                  <div className="w-16 h-16 border-4 border-blue-200 border-t-blue-600 rounded-full animate-spin"></div>
                  <div className="absolute inset-0 flex items-center justify-center">
                    <span className="text-2xl">üåæ</span>
                  </div>
                </div>
              </div>
              <h3 className="text-lg font-semibold text-gray-800 mb-2">
                Fetching Real-Time Crop Prices
              </h3>
              <p className="text-gray-600 mb-4 animate-pulse">
                {loadingQuote}
              </p>
              <div className="text-sm text-gray-500">
                <p>‚è±Ô∏è This may take 30-60 seconds as we search multiple government sources</p>
                <p>üìä We're getting the most accurate, up-to-date mandi prices for you</p>
              </div>
            </div>
          </div>
        )}
        
        {error && (
          <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-lg">
            <p className="text-red-600 text-center">{error}</p>
          </div>
        )}
      </div>

      {/* Results */}
      {prices && (
        <div className="bg-white rounded-xl shadow-lg p-6">
          <div className="text-center mb-6">
            <h3 className="text-2xl font-semibold text-green-700 mb-2">
              {getCropIcon(prices.crop_name)} {prices.crop_name} Prices in {prices.location}
            </h3>
            <p className="text-gray-600">
              Last updated: {prices.last_updated} ‚Ä¢ {prices.note}
            </p>
            {prices.pending && (
              <div className="mt-4 flex flex-col items-center">
                <span className="inline-flex items-center gap-2 px-4 py-2 bg-yellow-100 text-yellow-800 rounded-full text-sm font-medium animate-pulse">
                  <svg className="animate-spin h-4 w-4 mr-1 text-yellow-600" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path></svg>
                  Fetching real-time prices from government sources...
                </span>
                <button
                  className="mt-3 px-4 py-2 bg-green-600 text-white rounded-lg font-semibold hover:bg-green-700 transition-all duration-200"
                  onClick={handleRefresh}
                  disabled={loading}
                >
                  Refresh for Real Data
                </button>
                <span className="text-xs text-gray-500 mt-1">Estimated prices shown below. Real data may take up to a minute.</span>
              </div>
            )}
          </div>

          <div className="grid gap-6">
            {prices.mandi_prices.map((mandi, index) => (
              <div key={index} className="border border-gray-200 rounded-lg p-6 hover:shadow-md transition-shadow duration-200">
                <div className="flex items-center justify-between mb-4">
                  <h4 className="text-xl font-semibold text-gray-800">
                    üè™ {mandi.mandi_name}
                  </h4>
                  <div className="flex items-center gap-2">
                    <span className="px-3 py-1 bg-green-100 text-green-800 rounded-full text-sm font-medium">
                      {mandi.quality}
                    </span>
                    {mandi.source && (
                      <span className="px-2 py-1 bg-blue-100 text-blue-800 rounded-full text-xs font-medium">
                        {mandi.source}
                      </span>
                    )}
                  </div>
                </div>
                
                <div className="grid md:grid-cols-3 gap-4">
                  <div className="text-center p-4 bg-green-50 rounded-lg">
                    <p className="text-sm text-gray-600 mb-1">Current Price</p>
                    <p className="text-2xl font-bold text-green-600">
                      {formatPrice(mandi.price_per_quintal)}
                    </p>
                    <p className="text-xs text-gray-500">per quintal</p>
                  </div>
                  
                  <div className="text-center p-4 bg-blue-50 rounded-lg">
                    <p className="text-sm text-gray-600 mb-1">Min Price</p>
                    <p className="text-xl font-semibold text-blue-600">
                      {formatPrice(mandi.min_price)}
                    </p>
                    <p className="text-xs text-gray-500">per quintal</p>
                  </div>
                  
                  <div className="text-center p-4 bg-orange-50 rounded-lg">
                    <p className="text-sm text-gray-600 mb-1">Max Price</p>
                    <p className="text-xl font-semibold text-orange-600">
                      {formatPrice(mandi.max_price)}
                    </p>
                    <p className="text-xs text-gray-500">per quintal</p>
                  </div>
                </div>
                
                <div className="mt-4 pt-4 border-t border-gray-200">
                  <p className="text-sm text-gray-600">
                    <span className="font-medium">Price Range:</span> {formatPrice(mandi.min_price)} - {formatPrice(mandi.max_price)} per quintal
                  </p>
                </div>
              </div>
            ))}
          </div>

          {/* Market Insights */}
          <div className="mt-8 p-6 bg-gradient-to-r from-amber-50 to-orange-50 rounded-lg border border-amber-200">
            <h4 className="text-lg font-semibold text-gray-800 mb-3 flex items-center">
              üí° Market Insights
            </h4>
            <div className="text-sm text-gray-700">
              {prices.market_insights ? (
                <div className="whitespace-pre-line">{prices.market_insights}</div>
              ) : (
                <div className="grid md:grid-cols-2 gap-4">
                  <div>
                    <p className="font-medium mb-1">üìà Price Trend:</p>
                    <p>Prices are currently stable with moderate fluctuations</p>
                  </div>
                  <div>
                    <p className="font-medium mb-1">üïí Best Time to Sell:</p>
                    <p>Consider selling during peak demand seasons</p>
                  </div>
                  <div>
                    <p className="font-medium mb-1">üìä Market Demand:</p>
                    <p>Steady demand expected in the coming weeks</p>
                  </div>
                  <div>
                    <p className="font-medium mb-1">üíº Trading Volume:</p>
                    <p>High trading activity in major mandis</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Information Section */}
      <div className="mt-8 bg-white rounded-xl shadow-lg p-6">
        <h3 className="text-xl font-semibold text-gray-800 mb-4 text-center">
          üìã About Mandi Prices
        </h3>
        <div className="grid md:grid-cols-2 gap-6 text-sm text-gray-600">
          <div>
            <h4 className="font-semibold text-gray-800 mb-2">What are Mandi Prices?</h4>
            <p>
              Mandi prices are the wholesale rates at which agricultural commodities are traded 
              in government-regulated agricultural markets (mandis). These prices serve as 
              benchmarks for farmers and traders.
            </p>
          </div>
          <div>
            <h4 className="font-semibold text-gray-800 mb-2">How to Use This Information?</h4>
            <p>
              Compare prices across different mandis, track price trends, and make informed 
              decisions about when and where to sell your produce for maximum profit.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CropPricesForm;
