import React from 'react';
import { useNavigate } from 'react-router-dom';

// Accent color classes
const accentHeading = "text-yellow-300";
const accentIcon = "text-sky-300";
const accentHighlight = "bg-gradient-to-br from-yellow-900/30 via-emerald-950 to-green-800/90";

function Home() {
  const navigate = useNavigate();
  return (
    <section className="relative min-h-[80vh] flex flex-col items-center justify-center text-center py-16 bg-gradient-to-br from-green-950 via-green-900 to-[#5B4636] text-emerald-100">
      <div className="absolute inset-0 w-full h-full bg-gradient-to-br from-green-900/80 via-emerald-900/70 to-transparent pointer-events-none" />
      <div className="relative z-10 max-w-4xl mx-auto flex flex-col items-center">
        {/* Plant image and project info */}
        <img
          src="https://images.unsplash.com/photo-1501004318641-b39e6451bec6?auto=format&fit=crop&w=600&q=80"
          alt="Plant inspiration"
          className="w-36 h-36 object-cover rounded-full shadow-lg border-4 border-emerald-500 mb-8 bg-green-900"
        />
        <h1 className={`text-6xl md:text-7xl font-extrabold mb-8 drop-shadow-lg tracking-tight ${accentHeading}`}>AgriGuru</h1>
        <div className={`rounded-2xl shadow-2xl p-10 mb-12 max-w-3xl w-full text-left text-emerald-100 flex flex-col gap-10 border border-emerald-700/60 ${accentHighlight}`}>
          {/* About Section */}
          <div>
            <h2 className={`text-3xl font-extrabold mb-4 uppercase tracking-wide ${accentHeading}`}>About AgriGuru</h2>
            <div className="space-y-2">
              <div className="font-bold text-lg text-emerald-200">Empowering Indian Agriculture</div>
              <div className="font-semibold text-base text-yellow-100">AI-powered platform for personalized crop advice, weather, government schemes, organic tips, and satellite insights.</div>
            </div>
          </div>
          {/* Features Section */}
          <div>
            <h2 className={`text-3xl font-extrabold mb-4 uppercase tracking-wide ${accentHeading}`}>Key Features</h2>
            <ul className="grid grid-cols-1 md:grid-cols-2 gap-x-8 gap-y-3">
              <li className="font-bold text-sky-300 flex items-center gap-2">🌱 <span className="text-emerald-200">Crop Recommendation</span></li>
              <li className="font-bold text-yellow-200 flex items-center gap-2">☀️ <span className="text-emerald-200">Weather Forecast</span></li>
              <li className="font-bold text-orange-200 flex items-center gap-2">🧑‍🌾 <span className="text-emerald-200">Organic Tips</span></li>
              <li className="font-bold text-yellow-300 flex items-center gap-2">🏛️ <span className="text-emerald-200">Government Schemes</span></li>
              <li className="font-bold text-sky-300 flex items-center gap-2">💰 <span className="text-emerald-200">Crop Prices (Mandi Rates)</span></li>
              <li className="font-bold text-sky-300 flex items-center gap-2">🤖 <span className="text-emerald-200">AI Chatbot (English, Hindi, Kannada)</span></li>
              <li className="font-bold text-yellow-200 flex items-center gap-2">🛰️ <span className="text-emerald-200">Satellite Insight (NDVI & Crop Health)</span></li>
            </ul>
          </div>
          {/* What Sets Us Apart Section */}
          <div>
            <h2 className={`text-3xl font-extrabold mb-4 uppercase tracking-wide ${accentHeading}`}>Why Choose Us?</h2>
            <ul className="space-y-2">
              <li><span className="font-bold text-yellow-200">AI-Driven:</span> <span className="text-emerald-100">Region-specific, actionable recommendations</span></li>
              <li><span className="font-bold text-sky-300">Integrated:</span> <span className="text-emerald-100">Weather, satellite, and government data in one place</span></li>
              <li><span className="font-bold text-orange-200">Inclusive:</span> <span className="text-emerald-100">Multilingual, mobile-first, and accessible</span></li>
              <li><span className="font-bold text-yellow-300">Farmer-Focused:</span> <span className="text-emerald-100">Designed for real-world impact</span></li>
            </ul>
          </div>
          {/* Inspiration Section */}
          <div>
            <h2 className={`text-3xl font-extrabold mb-4 uppercase tracking-wide ${accentHeading}`}>Our Inspiration</h2>
            <div className="font-bold text-yellow-200 mb-1">Rooted in Resilience</div>
            <div className="font-semibold text-base text-sky-100">Inspired by the spirit of Indian farmers, AgriGuru bridges technology and tradition for a sustainable future.</div>
          </div>
        </div>
        <button onClick={() => navigate('/crop-recommendation')} className="bg-yellow-400 hover:bg-yellow-500 text-green-900 font-bold px-12 py-4 rounded-xl shadow-lg hover:scale-105 transition-all text-xl mb-10">Get Started</button>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-10 mt-2 w-full">
          <FeatureCard title="Crop Recommendation" desc="AI-driven suggestions for the best crops based on your soil, season, and location." accent="sky" />
          <FeatureCard title="Weather Forecast" desc="Accurate, location-based 5-day weather forecasts to plan your farming activities." accent="yellow" />
          <FeatureCard title="Organic Tips" desc="Expert tips for sustainable and organic farming at every stage of your crop cycle." accent="orange" />
          <FeatureCard title="Government Schemes" desc="Explore the latest government schemes and subsidies for farmers in your region." accent="yellow" />
          <FeatureCard title="Crop Prices" desc="Get the latest mandi prices for crops in your area to make informed selling decisions." accent="sky" />
          <FeatureCard title="AI Chatbot" desc="Get instant answers to your farming questions in English, Hindi, or Kannada, powered by Gemini AI." accent="sky" />
          <FeatureCard title="Satellite Insight" desc="Get NDVI and crop health insights from satellite data for your field or village." accent="yellow" />
        </div>
      </div>
    </section>
  );
}

function FeatureCard({ title, desc, accent }) {
  const accentMap = {
    sky: 'from-green-900 via-emerald-950 to-sky-900/80 border-sky-400',
    yellow: 'from-green-900 via-yellow-900 to-green-800/90 border-yellow-400',
    orange: 'from-green-900 via-emerald-950 to-orange-900/80 border-orange-400',
    default: 'from-green-900 via-emerald-950 to-green-800/90 border-emerald-700/60',
  };
  const cardClass = `bg-gradient-to-br rounded-xl shadow-md p-6 border hover:border-emerald-400 transition flex flex-col gap-2 ${accentMap[accent] || accentMap.default}`;
  const titleClass = accent === 'sky' ? 'text-sky-200' : accent === 'yellow' ? 'text-yellow-200' : accent === 'orange' ? 'text-orange-200' : 'text-emerald-200';
  return (
    <div className={cardClass}>
      <h3 className={`text-xl font-bold mb-1 ${titleClass}`}>{title}</h3>
      <p className="text-emerald-100 text-base leading-relaxed">{desc}</p>
    </div>
  );
}

export default Home;
