import React, { useState } from 'react';
import { recommendCrop } from '../api/api';

const soilTypes = ['Sandy', 'Clay', 'Silty', 'Peaty', 'Chalky', 'Loamy'];
const seasons = ['Kharif', 'Rabi', 'Zaid'];

function CropRecommendationForm() {
  const [form, setForm] = useState({ soil: '', season: '', location: '' });
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); setError(null); setResult(null);
    try {
      const res = await recommendCrop({ ...form, user_id: 'guest' });
      setResult(res.data);
    } catch (err) {
      setError('Failed to get recommendation');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-xl font-semibold mb-2">Crop Recommendation</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-2">
        <select className="border p-2" required value={form.soil} onChange={e => setForm({ ...form, soil: e.target.value })}>
          <option value="">Select Soil Type</option>
          {soilTypes.map(type => <option key={type} value={type}>{type}</option>)}
        </select>
        <select className="border p-2" required value={form.season} onChange={e => setForm({ ...form, season: e.target.value })}>
          <option value="">Select Season</option>
          {seasons.map(season => <option key={season} value={season}>{season}</option>)}
        </select>
        <input className="border p-2" required placeholder="Enter Location" value={form.location} onChange={e => setForm({ ...form, location: e.target.value })} />
        <button className="bg-green-600 text-white p-2 rounded" type="submit">Get Recommendation</button>
      </form>
      {loading && <div className="mt-2 text-blue-600">Loading...</div>}
      {error && <div className="mt-2 text-red-600">{error}</div>}
      {result && (
        <div className="mt-2 p-2 bg-green-50 border rounded">
          <div><b>Suggested Crops:</b> {Array.isArray(result.crops) ? result.crops.join(', ') : result.crops}</div>
          <div><b>Reason:</b> {result.reason}</div>
        </div>
      )}
    </div>
  );
}
export default CropRecommendationForm;
