import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';

const menu = [
  { name: 'Home', path: '/' },
  { name: 'Crop Recommendation', path: '/crop-recommendation' },
  { name: 'Weather', path: '/weather' },
  { name: 'Organic Tips', path: '/tips' },
  { name: 'Government Schemes', path: '/schemes' },
  { name: 'Chatbot', path: '/chatbot' },
  { name: 'Satellite Insight', path: '/satellite-insight' },
];

function Navbar() {
  const [open, setOpen] = useState(false);
  const [aboutOpen, setAboutOpen] = useState(false);
  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-background/95 shadow-md backdrop-blur border-b border-primary-dark">
      <div className="max-w-6xl mx-auto px-4 flex items-center justify-between h-16">
        <Link to="/" className="flex items-center text-2xl font-bold text-primary gap-2">
          <span role="img" aria-label="logo">ðŸŒ¾</span> AgriGuru
        </Link>
        <div className="flex items-center gap-2">
          <button className="md:hidden p-2" onClick={() => setOpen(!open)}>
            <svg className="w-7 h-7 text-primary" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={open ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'} />
            </svg>
          </button>
          {/* About Icon Button */}
          <button
            className="ml-2 p-2 rounded-full hover:bg-background-light transition flex items-center justify-center"
            aria-label="About the Developers"
            onClick={() => setAboutOpen(true)}
          >
            <svg className="w-7 h-7 text-primary" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
              <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2" />
              <path strokeLinecap="round" strokeLinejoin="round" d="M12 16v-4" />
              <circle cx="12" cy="8.5" r="1" fill="currentColor" />
            </svg>
          </button>
        </div>
        <div className={`md:flex gap-2 font-medium items-center ${open ? 'block absolute bg-background left-0 right-0 top-16 shadow-lg p-4 rounded-b-xl z-40' : 'hidden md:flex'}`}>
          {menu.map(item => (
            <NavLink
              key={item.path}
              to={item.path}
              onClick={() => setOpen(false)}
              className={({ isActive }) =>
                `px-4 py-2 mx-1 rounded-lg transition-all duration-150 text-base flex items-center justify-center
                ${isActive ? 'bg-accent text-background font-bold shadow' : 'hover:bg-background-light hover:text-accent'}`
              }
            >
              {item.name}
            </NavLink>
          ))}
        </div>
        {/* About Modal placeholder - will be implemented next */}
        {aboutOpen && null}
      </div>
    </nav>
  );
}

export default Navbar;
