function FeatureCard({ title, desc, variant }) {
  // variant: 'primary' | 'accent'
  const bg = variant === 'accent' ? 'bg-accent' : 'bg-primary';
  const text = variant === 'accent' ? 'text-text' : 'text-text-light';
  const border = variant === 'accent' ? 'border-accent' : 'border-primary';
  return (
    <div className={`card flex flex-col gap-2 group cursor-pointer hover:scale-[1.03] active:scale-100 transition-all duration-200 border ${border} ${bg} ${text}`}
      style={{ boxShadow: '0 4px 24px 0 rgba(93,138,60,0.10)' }}>
      <h3 className={`text-xl font-bold mb-1 font-heading group-hover:text-clay transition-colors duration-200`} style={{textDecoration: 'none'}}>{title}</h3>
      <p className={`text-base leading-relaxed font-sans opacity-90 group-hover:opacity-100 transition-opacity duration-200`} style={{textDecoration: 'none'}}>{desc}</p>
    </div>
  );
}

function TeamMember({ name, role, part }) {
  return (
    <div className="flex flex-col items-center bg-card rounded-xl p-4 shadow-soft w-full border border-background text-text">
      <div className="text-lg font-bold mb-1">{name}</div>
      <div className="text-text/80 text-sm font-medium text-center mb-1">{role}</div>
      <div className="text-xs text-text text-center">{part}</div>
    </div>
  );
}

export default Home;
