import React, { useEffect, useState } from 'react';
import { getTips, getAITip } from '../api/api';

const LANGUAGES = [
  { code: 'en', label: 'English' },
  { code: 'hi', label: 'Hindi' },
  { code: 'kn', label: 'Kannada' },
];

function TipsSection() {
  const [tips, setTips] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [query, setQuery] = useState("");
  const [answer, setAnswer] = useState(null);
  const [answerLoading, setAnswerLoading] = useState(false);
  const [language, setLanguage] = useState('en');

  useEffect(() => {
    setLoading(true); setError(null);
    getTips().then(res => setTips(res.data)).catch(() => setError('Failed to fetch tips')).finally(() => setLoading(false));
  }, []);

  const handleAsk = async (e) => {
    e.preventDefault();
    setAnswer(null); setAnswerLoading(true);
    try {
      const res = await getAITip({ question: query, language });
      setAnswer(res.data.answer);
    } catch (err) {
      setAnswer('Sorry, the AI could not answer your question at this time.');
    } finally {
      setAnswerLoading(false);
    }
  };

  return (
    <div className="bg-card rounded-2xl shadow-lg p-6 border border-background-light">
      <h2 className="text-2xl font-bold text-accent mb-3 flex items-center gap-2">
        <span role="img" aria-label="tips">🌱</span> Organic Farming Tips
      </h2>
      {loading && <div className="text-accent">Loading...</div>}
      {error && <div className="text-red-400 bg-background-light rounded p-2 border border-red-600 animate-pulse">{error}</div>}
      <ul className="list-disc ml-6 mb-6 text-gray-100">
        {tips.map((tip, i) => (
          <li key={i} className="mb-1"><b className="text-accent">{tip.category} ({tip.stage}):</b> {tip.tip}</li>
        ))}
      </ul>
      <form onSubmit={handleAsk} className="flex flex-col sm:flex-row gap-2 items-stretch mb-2">
        <input
          className="border border-background-light bg-background text-gray-100 rounded-lg p-2 flex-1 focus:ring-2 focus:ring-accent outline-none transition"
          placeholder="Ask a question about organic farming, crops, or soil..."
          value={query}
          onChange={e => setQuery(e.target.value)}
          required
        />
        <select
          className="border border-background-light bg-background text-gray-900 rounded-lg p-2 focus:ring-2 focus:ring-accent outline-none transition font-semibold"
          value={language}
          onChange={e => setLanguage(e.target.value)}
        >
          {LANGUAGES.map(lang => (
            <option key={lang.code} value={lang.code}>{lang.label}</option>
          ))}
        </select>
        <button
          className="bg-accent hover:bg-primary text-background font-semibold p-2 rounded-lg shadow transition-all duration-200"
          type="submit"
          disabled={answerLoading || !query.trim()}
        >{answerLoading ? (
          <span className="flex items-center gap-2">
            <svg className="animate-spin h-5 w-5 text-background" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path></svg>
            Thinking...
          </span>
        ) : 'Ask'}</button>
      </form>
      {answerLoading && (
        <div className="mt-3 p-4 bg-gradient-to-r from-green-50 to-emerald-50 border border-green-200 rounded-xl animate-pulse text-center text-accent font-semibold">
          <span role="img" aria-label="ai">🤖</span> The AI is thinking and preparing a precise answer...
        </div>
      )}
      {answer && !answerLoading && (
        <div className="mt-4 p-4 bg-white border border-emerald-200 rounded-xl shadow text-gray-900 text-lg font-medium whitespace-pre-line animate-fade-in">
          <span className="block mb-2 text-accent font-bold">AI Answer:</span>
          {answer}
        </div>
      )}
    </div>
  );
}
export default TipsSection;
