import React from 'react';
import CropRecommendationForm from './components/CropRecommendationForm';
import WeatherCard from './components/WeatherCard';
import TipsSection from './components/TipsSection';
import SchemesExplorer from './components/SchemesExplorer';
import Chatbot from './components/Chatbot';
import CropHealthIndicator from './components/CropHealthIndicator';
import Dashboard from './components/Dashboard';

function App() {
  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-green-700 text-white p-4 text-2xl font-bold text-center">AgriGuru</header>
      <main className="p-4 flex flex-col gap-6 max-w-4xl mx-auto">
        <Dashboard />
        <CropRecommendationForm />
        <WeatherCard />
        <TipsSection />
        <SchemesExplorer />
        <CropHealthIndicator />
        <Chatbot />
      </main>
    </div>
  );
}

export default App;
