import React, { useState } from 'react';
import { getSatelliteInsight } from '../api/api';

function NDVICircle({ value, color }) {
  // value: 0-1, color: green/yellow/red
  const percent = Math.round(value * 100);
  const stroke = 8;
  const radius = 32;
  const normalizedRadius = radius - stroke / 2;
  const circumference = normalizedRadius * 2 * Math.PI;
  const strokeDashoffset = circumference - (percent / 100) * circumference;
  return (
    <svg height={radius * 2} width={radius * 2} className="block mx-auto">
      <circle
        stroke="#444"
        fill="none"
        strokeWidth={stroke}
        r={normalizedRadius}
        cx={radius}
        cy={radius}
      />
      <circle
        stroke={color === 'green' ? '#22c55e' : color === 'yellow' ? '#eab308' : '#ef4444'}
        fill="none"
        strokeWidth={stroke}
        strokeLinecap="round"
        strokeDasharray={circumference + ' ' + circumference}
        style={{ strokeDashoffset, transition: 'stroke-dashoffset 0.5s' }}
        r={normalizedRadius}
        cx={radius}
        cy={radius}
      />
      <text
        x="50%"
        y="50%"
        textAnchor="middle"
        dy=".3em"
        fontSize="1.1rem"
        fill="#fff"
        fontWeight="bold"
      >
        {value}
      </text>
    </svg>
  );
}

function SatelliteInsightPage() {
  const [location, setLocation] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showInfo, setShowInfo] = useState(false);

  const handleFetch = async (e) => {
    e.preventDefault();
    setLoading(true); setError(null); setResult(null);
    try {
      const res = await getSatelliteInsight({ location });
      setResult(res.data);
    } catch (err) {
      setError('Failed to fetch satellite insight');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="max-w-xl mx-auto py-10 px-4 bg-card rounded-xl shadow-lg text-gray-100">
      <h2 className="text-3xl font-bold text-accent mb-6 flex items-center gap-2">
        <span role="img" aria-label="satellite">🛰️</span> Satellite Insight
      </h2>
      <form onSubmit={handleFetch} className="flex flex-col sm:flex-row gap-3 mb-6">
        <input
          className="border border-background-light bg-background text-gray-100 rounded-lg p-2 flex-1 focus:ring-2 focus:ring-accent outline-none transition"
          placeholder="Enter location (city, village, etc.)"
          value={location}
          onChange={e => setLocation(e.target.value)}
          required
        />
        <button className="bg-accent hover:bg-primary text-background font-semibold p-2 rounded-lg shadow transition-all duration-200" type="submit" disabled={loading}>{loading ? 'Loading...' : 'Get Insight'}</button>
      </form>
      {error && <div className="text-red-400 bg-background-light rounded p-2 border border-red-600 animate-pulse mb-2">{error}</div>}
      {result && (
        <div className="bg-background-light rounded-xl shadow p-6 border border-background flex flex-col items-center w-full">
          <div className="w-full flex flex-col sm:flex-row justify-between items-center mb-4">
            <div className="flex flex-col items-center mb-2 sm:mb-0">
              <span className="text-2xl">📍</span>
              <span className="text-gray-300 text-sm font-semibold">Location</span>
              <span className="text-accent font-bold text-lg">{result.location}</span>
              <span className="text-gray-400 text-xs">Lat: {result.lat.toFixed(3)} | Lon: {result.lon.toFixed(3)}</span>
            </div>
            <div className="flex flex-col items-center">
              <span className="text-2xl">🌱</span>
              <span className="text-gray-300 text-sm font-semibold">NDVI</span>
              <div className="relative flex flex-col items-center">
                <NDVICircle value={result.ndvi} color={result.color} />
                <span className={`absolute top-0 right-0 px-2 py-0.5 rounded-full text-xs font-bold ${result.color === 'green' ? 'bg-green-500 text-background' : result.color === 'yellow' ? 'bg-yellow-400 text-background' : 'bg-red-500 text-background'}`}>{result.status}</span>
              </div>
            </div>
          </div>
          <div className="w-full flex flex-col items-center mb-4">
            <span className="text-2xl">🩺</span>
            <span className="text-gray-300 text-sm font-semibold">Crop Health</span>
            <span className={`font-bold text-lg ${result.color === 'green' ? 'text-green-400' : result.color === 'yellow' ? 'text-yellow-300' : 'text-red-400'}`}>{result.status}</span>
            <div className="text-gray-100 text-center mt-1">{result.recommendation}</div>
          </div>
          {/* Satellite image preview */}
          {result.lat && result.lon && (
            <div className="w-full flex flex-col items-center mb-4">
              <span className="text-gray-300 text-sm font-semibold mb-1">Satellite Image Preview</span>
              <img
                src={`https://maps.openweathermap.org/maps/2.0/weather/SATELLITE/10/${result.lon},${result.lat}?appid=${process.env.REACT_APP_OPENWEATHER_API_KEY}`}
                alt="Satellite preview"
                className="rounded-lg shadow border border-background max-w-xs"
                style={{ minHeight: 120, background: '#222' }}
                onError={e => { e.target.style.display = 'none'; }}
              />
              <span className="text-xs text-gray-400 mt-1">Powered by OpenWeatherMap Satellite</span>
            </div>
          )}
          <button
            className="mt-2 text-xs text-accent underline hover:text-primary"
            onClick={() => setShowInfo(v => !v)}
            type="button"
          >
            {showInfo ? 'Hide' : 'What is NDVI & Crop Health?'}
          </button>
          {showInfo && (
            <div className="mt-2 p-3 bg-background border-l-4 border-accent rounded shadow text-gray-100 text-sm w-full">
              <b>NDVI (Normalized Difference Vegetation Index):</b> NDVI is a satellite-derived value between 0 and 1 that measures crop greenness and health. Higher values (closer to 1) mean healthier, greener crops. <br />
              <b>Crop Health:</b> Based on NDVI, this shows if crops are healthy, moderate, or stressed. <br />
              <span className="text-xs text-gray-400">🟢 Healthy: NDVI &gt; 0.7 &nbsp; | &nbsp; 🟡 Moderate: 0.5 - 0.7 &nbsp; | &nbsp; 🔴 Unhealthy: &lt; 0.5</span>
            </div>
          )}
        </div>
      )}
    </section>
  );
}

export default SatelliteInsightPage;
