import React from 'react';
import { useNavigate } from 'react-router-dom';

// Accent color classes
const accentHeading = "text-green-700";
const accentIcon = "text-green-500";
const accentHighlight = "bg-gradient-to-br from-green-100 via-green-50 to-emerald-50";

function Home() {
  const navigate = useNavigate();
  return (
    <section className="relative min-h-[80vh] flex flex-col items-center justify-center text-center bg-darkBg text-text">
      <div className="relative z-10 max-w-4xl mx-auto flex flex-col items-center py-section md:py-section-lg">
        <img
          src="https://images.unsplash.com/photo-1501004318641-b39e6451bec6?auto=format&fit=crop&w=600&q=80"
          alt="Plant inspiration"
          className="w-36 h-36 object-cover rounded-full shadow-card border-4 border-emerald mb-8 bg-darkBg"
        />
        <h1>AgriGuru</h1>
        <div className="rounded-2xl shadow-card p-10 mb-12 max-w-3xl w-full text-left bg-darkBg flex flex-col gap-10 border border-border">
          {/* About Section */}
          <div>
            <h2>About AgriGuru</h2>
            <div className="space-y-2">
              <div className="font-bold text-lg text-emerald">Empowering Indian Agriculture</div>
              <div className="font-semibold text-base text-sage">AI-powered platform for personalized crop advice, weather, government schemes, organic tips, and satellite insights.</div>
            </div>
          </div>
          <div className="divider" />
          {/* Features Section */}
          <div>
            <h2>Key Features</h2>
            <ul className="grid grid-cols-1 md:grid-cols-2 gap-x-8 gap-y-3">
              <li className="font-bold text-emerald flex items-center gap-2">🌱 <span className="text-text">Crop Recommendation</span></li>
              <li className="font-bold text-emerald flex items-center gap-2">☀️ <span className="text-text">Weather Forecast</span></li>
              <li className="font-bold text-emerald flex items-center gap-2">🧑‍🌾 <span className="text-text">Organic Tips</span></li>
              <li className="font-bold text-moss flex items-center gap-2">🏛️ <span className="text-text">Government Schemes</span></li>
              <li className="font-bold text-emerald flex items-center gap-2">💰 <span className="text-text">Crop Prices (Mandi Rates)</span></li>
              <li className="font-bold text-emerald flex items-center gap-2">🤖 <span className="text-text">AI Chatbot (English, Hindi, Kannada)</span></li>
              <li className="font-bold text-emerald flex items-center gap-2">🛰️ <span className="text-text">Satellite Insight (NDVI & Crop Health)</span></li>
            </ul>
          </div>
          <div className="divider" />
          {/* What Sets Us Apart Section */}
          <div>
            <h2>Why Choose Us?</h2>
            <ul className="space-y-2">
              <li><span className="font-bold text-emerald">AI-Driven:</span> <span className="text-text">Region-specific, actionable recommendations</span></li>
              <li><span className="font-bold text-emerald">Integrated:</span> <span className="text-text">Weather, satellite, and government data in one place</span></li>
              <li><span className="font-bold text-emerald">Inclusive:</span> <span className="text-text">Multilingual, mobile-first, and accessible</span></li>
              <li><span className="font-bold text-moss">Farmer-Focused:</span> <span className="text-text">Designed for real-world impact</span></li>
            </ul>
          </div>
          <div className="divider" />
          {/* Inspiration Section */}
          <div>
            <h2>Our Inspiration</h2>
            <div className="font-bold text-emerald mb-1">Rooted in Resilience</div>
            <div className="font-semibold text-base text-sage">Inspired by the spirit of Indian farmers, AgriGuru bridges technology and tradition for a sustainable future.</div>
          </div>
        </div>
        <button onClick={() => navigate('/crop-recommendation')} className="btn mb-10 hover:scale-105 active:scale-100 transition-transform duration-150">Get Started</button>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-10 mt-2 w-full">
          <FeatureCard title="Crop Recommendation" desc="AI-driven suggestions for the best crops based on your soil, season, and location." accent="emerald" />
          <FeatureCard title="Weather Forecast" desc="Accurate, location-based 5-day weather forecasts to plan your farming activities." accent="moss" />
          <FeatureCard title="Organic Tips" desc="Expert tips for sustainable and organic farming at every stage of your crop cycle." accent="emerald" />
          <FeatureCard title="Government Schemes" desc="Explore the latest government schemes and subsidies for farmers in your region." accent="moss" />
          <FeatureCard title="Crop Prices" desc="Get the latest mandi prices for crops in your area to make informed selling decisions." accent="emerald" />
          <FeatureCard title="AI Chatbot" desc="Get instant answers to your farming questions in English, Hindi, or Kannada, powered by Gemini AI." accent="moss" />
          <FeatureCard title="Satellite Insight" desc="Get NDVI and crop health insights from satellite data for your field or village." accent="emerald" />
        </div>
      </div>
    </section>
  );
}

function FeatureCard({ title, desc, accent }) {
  const accentMap = {
    emerald: 'from-darkBg via-darkBg to-emerald/10 border-emerald',
    moss: 'from-darkBg via-darkBg to-moss/10 border-moss',
    default: 'from-darkBg via-darkBg to-emerald/10 border-emerald',
  };
  const cardClass = `bg-gradient-to-br rounded-2xl shadow-card p-6 border hover:border-emerald transition-all duration-200 flex flex-col gap-2 group cursor-pointer hover:scale-[1.03] active:scale-100` + ' ' + (accentMap[accent] || accentMap.default);
  const titleClass = 'text-emerald font-heading group-hover:text-moss transition-colors duration-200';
  return (
    <div className={cardClass} tabIndex={0} aria-label={title}>
      <h3 className={`text-xl font-bold mb-1 ${titleClass}`}>{title}</h3>
      <p className="text-text text-base leading-relaxed font-sans opacity-90 group-hover:opacity-100 transition-opacity duration-200">{desc}</p>
    </div>
  );
}

export default Home;
