import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';

const menu = [
  { name: 'Home', path: '/', icon: 'üè°', desc: 'Start' },
  { name: 'Crop Recommendation', path: '/crop-recommendation', icon: 'üå±', desc: 'Best Crops' },
  { name: 'Weather', path: '/weather', icon: '‚òÄÔ∏è', desc: 'Forecast' },
  { name: 'Organic Tips', path: '/tips', icon: 'üçÉ', desc: 'Farming Advice' },
  { name: 'Government Schemes', path: '/schemes', icon: 'üèõÔ∏è', desc: 'Schemes' },
  { name: 'Dashboard', path: '/dashboard', icon: 'üìä', desc: 'Your Data' },
  { name: 'Chatbot', path: '/chatbot', icon: 'ü§ñ', desc: 'Ask AI' }
];

function Navbar() {
  const [open, setOpen] = useState(false);
  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-gradient-to-r from-green-900 via-green-700 to-green-600 bg-[url('https://www.transparenttextures.com/patterns/diamond-upholstery.png')] bg-repeat shadow-lg backdrop-blur-lg border-b-4 border-emerald-400/80">
      <div className="max-w-6xl mx-auto px-4 flex items-center justify-between h-16">
        <Link to="/" className="flex items-center text-3xl font-extrabold text-white drop-shadow-lg gap-3 tracking-wide">
          <span role="img" aria-label="logo" className="text-4xl">üöú</span> AgriGuru
        </Link>
        <button className="md:hidden p-2" onClick={() => setOpen(!open)}>
          <svg className="w-7 h-7 text-green-700" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={open ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'} />
          </svg>
        </button>
        <div className={`md:grid md:grid-cols-7 gap-2 font-medium text-center ${open ? 'block absolute bg-white left-0 right-0 top-16 shadow-lg p-4' : 'hidden md:grid'}`} style={{ minWidth: 0 }}>
          {menu.map(item => {
            // Assign a unique background per feature
            let bg = '';
            switch (item.name) {
              case 'Home':
                bg = 'bg-gradient-to-br from-green-200 via-green-100 to-green-50 bg-[url(https://svgshare.com/i/17rJ.svg)] bg-cover'; break; // subtle field
              case 'Crop Recommendation':
                bg = 'bg-gradient-to-br from-lime-100 via-green-50 to-yellow-50 bg-[url(https://svgshare.com/i/17qz.svg)] bg-cover'; break; // crops
              case 'Weather':
                bg = 'bg-gradient-to-br from-sky-100 via-blue-50 to-yellow-50 bg-[url(https://svgshare.com/i/17qY.svg)] bg-cover'; break; // sun/cloud
              case 'Organic Tips':
                bg = 'bg-gradient-to-br from-emerald-100 via-green-50 to-lime-50 bg-[url(https://svgshare.com/i/17qX.svg)] bg-cover'; break; // leaves
              case 'Government Schemes':
                bg = 'bg-gradient-to-br from-yellow-100 via-green-50 to-amber-50 bg-[url(https://svgshare.com/i/17qW.svg)] bg-cover'; break; // document/rupee
              case 'Dashboard':
                bg = 'bg-gradient-to-br from-green-200 via-emerald-100 to-blue-50 bg-[url(https://svgshare.com/i/17qV.svg)] bg-cover'; break; // chart
              case 'Chatbot':
                bg = 'bg-gradient-to-br from-green-100 via-slate-50 to-blue-100 bg-[url(https://svgshare.com/i/17qU.svg)] bg-cover'; break; // chat bubble
              default:
                bg = 'bg-green-50';
            }
            return (
              <NavLink
                key={item.path}
                to={item.path}
                onClick={() => setOpen(false)}
                className={({ isActive }) =>
                  `relative flex flex-col items-center justify-center px-2 py-1 rounded-lg whitespace-nowrap overflow-hidden outline-none transition-all duration-200 ease-in-out
                  ${bg} 
                  ${isActive ? 'bg-green-100 text-green-700 font-semibold shadow border-b-2 border-green-600' :
                    'hover:bg-green-50 hover:text-green-700 focus-visible:ring-2 focus-visible:ring-green-400 focus-visible:ring-offset-2 hover:scale-105 hover:shadow-md'}
                  `
                }
              >
                <span className="text-xl mb-1 z-10">{item.icon}</span>
                <span className="text-xs font-medium leading-tight z-10">{item.desc}</span>
                <span className="text-[13px] font-normal hidden md:block z-10">{item.name}</span>
                <span className="absolute inset-0 opacity-10 pointer-events-none" />
              </NavLink>
            );
          })}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
