import React, { useState } from 'react';
import { askChatbot } from '../api/api';

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    setMessages([...messages, { from: 'user', text: input }]);
    setLoading(true); setError(null);
    try {
      const res = await askChatbot({ message: input, language: 'hi' });
      setMessages(msgs => [...msgs, { from: 'bot', text: res.data.answer }]);
    } catch (err) {
      setError('Chatbot error');
    } finally {
      setLoading(false);
      setInput('');
    }
  };

  return (
    <div className="bg-white p-4 rounded shadow">
      <h2 className="text-xl font-semibold mb-2">AgriGuru Chatbot</h2>
      <div className="h-32 overflow-y-auto border p-2 mb-2 bg-gray-50">
        {messages.map((msg, i) => (
          <div key={i} className={msg.from === 'user' ? 'text-right' : 'text-left'}>
            <span className={msg.from === 'user' ? 'bg-green-100' : 'bg-gray-200'} style={{ padding: '2px 8px', borderRadius: '8px', display: 'inline-block', margin: '2px 0' }}>{msg.text}</span>
          </div>
        ))}
      </div>
      {loading && <div className="text-blue-600">Thinking...</div>}
      {error && <div className="text-red-600">{error}</div>}
      <form onSubmit={handleSend} className="flex gap-2">
        <input className="border p-2 flex-1" value={input} onChange={e => setInput(e.target.value)} placeholder="Ask a question (Hindi supported)" />
        <button className="bg-green-600 text-white p-2 rounded" type="submit">Send</button>
      </form>
    </div>
  );
}
export default Chatbot;
