import React, { useState } from 'react';
import { recommendCrop } from '../api/api';

const soilTypes = [
  { name: 'Sandy', img: 'https://upload.wikimedia.org/wikipedia/commons/4/4d/Sandy_soil.jpg' },
  { name: 'Clay', img: 'https://upload.wikimedia.org/wikipedia/commons/8/8f/Clay_soil.jpg' },
  { name: 'Silty', img: 'https://upload.wikimedia.org/wikipedia/commons/7/7c/Silt_soil.jpg' },
  { name: 'Peaty', img: 'https://upload.wikimedia.org/wikipedia/commons/2/2e/Peaty_soil.jpg' },
  { name: 'Chalky', img: 'https://upload.wikimedia.org/wikipedia/commons/8/8e/Chalky_soil.jpg' },
  { name: 'Loamy', img: 'https://upload.wikimedia.org/wikipedia/commons/0/0c/Loamy_soil.jpg' }
];
const seasons = ['Summer', 'Autumn', 'Winter', 'Spring', 'Monsoon'];
const loadingQuotes = [
  "Good crops come from good choices. Fetching the best options for you...",
  "Patience is the key to a bountiful harvest. Gathering expert recommendations...",
  "The right crop for the right season makes all the difference!",
  "Soil and season are the roots of success. Finding your best match..."
];

function parseMarkdownTable(md) {
  if (!md) return null;
  const lines = md.trim().split('\n').filter(l => l.trim().length > 0);
  if (lines.length < 2) return null;
  const header = lines[0].replace(/^\|/, '').replace(/\|$/, '').split('|').map(h => h.trim());
  const rows = lines.slice(2).map(line =>
    line.replace(/^\|/, '').replace(/\|$/, '').split('|').map(cell => cell.trim())
  ).filter(row => row.length === header.length);
  return { header, rows };
}

function CropRecommendationForm() {
  const [form, setForm] = useState({ soil: '', season: '', location: '' });
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [quoteIdx, setQuoteIdx] = useState(0);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); setError(null); setResult(null);
    setQuoteIdx(Math.floor(Math.random() * loadingQuotes.length));
    try {
      const res = await recommendCrop({ ...form, user_id: 'guest' });
      setResult(res.data);
    } catch (err) {
      setError('Failed to get recommendation');
    } finally {
      setLoading(false);
    }
  };

  const parsedTable = result && result.table ? parseMarkdownTable(result.table) : null;

  return (
    <div className="bg-card rounded-2xl shadow-lg p-6 border border-background-light transition-transform hover:scale-[1.02] max-w-2xl mx-auto">
      <div className="flex items-center gap-3 mb-3">
        <span className="inline-flex items-center justify-center bg-primary text-white rounded-full w-10 h-10 shadow-md">
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
            <path strokeLinecap="round" strokeLinejoin="round" d="M12 2.25v19.5m9.75-9.75H2.25" />
          </svg>
        </span>
        <h2 className="text-2xl font-bold text-accent tracking-tight">Crop Recommendation</h2>
      </div>
      <form onSubmit={handleSubmit} className="flex flex-col gap-3">
        <div>
          <div className="font-semibold mb-2 text-accent">Choose Soil Type:</div>
          <div className="grid grid-cols-2 sm:grid-cols-3 gap-3 mb-2">
            {soilTypes.map(type => (
              <button
                type="button"
                key={type.name}
                className={`group border-2 rounded-xl flex flex-col items-center p-2 transition shadow hover:shadow-lg focus:outline-none ${form.soil === type.name ? 'border-accent bg-background-light' : 'border-background-light bg-background'}`}
                onClick={() => setForm({ ...form, soil: type.name })}
              >
                <img src={type.img} alt={type.name + ' soil'} className="w-24 h-24 object-cover rounded-xl mb-2 border border-background-light group-hover:scale-105 transition" onError={e => { e.target.onerror = null; e.target.src = 'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80'; }} />
                <span className="text-gray-100 font-medium">{type.name}</span>
              </button>
            ))}
          </div>
        </div>
        <select className="border border-background-light bg-background text-gray-100 rounded-lg p-2 focus:ring-2 focus:ring-accent outline-none transition" required value={form.season} onChange={e => setForm({ ...form, season: e.target.value })}>
          <option value="">Season</option>
          {seasons.map(season => <option key={season} value={season}>{season}</option>)}
        </select>
        <input className="border border-background-light bg-background text-gray-100 rounded-lg p-2 focus:ring-2 focus:ring-accent outline-none transition" placeholder="Enter Location (optional)" value={form.location} onChange={e => setForm({ ...form, location: e.target.value })} />
        <button className="bg-primary hover:bg-primary-dark text-white font-semibold p-2 rounded-lg shadow transition-all duration-200" type="submit" disabled={loading}>{loading ? 'Loading...' : 'Get Recommendation'}</button>
      </form>
      {loading && (
        <div className="mt-8 text-center">
          <div className="bg-gradient-to-r from-green-50 to-emerald-50 rounded-xl p-8 border border-green-200">
            <div className="flex items-center justify-center mb-4">
              <div className="relative">
                <div className="w-16 h-16 border-4 border-green-200 border-t-green-600 rounded-full animate-spin"></div>
                <div className="absolute inset-0 flex items-center justify-center">
                  <span className="text-2xl">üå±</span>
                </div>
              </div>
            </div>
            <h3 className="text-lg font-semibold text-gray-800 mb-2">
              Finding the best crops for your field...
            </h3>
            <p className="text-gray-600 mb-4 animate-pulse">
              {loadingQuotes[quoteIdx]}
            </p>
            <div className="text-sm text-gray-500">
              <p>‚è±Ô∏è This may take 10-20 seconds as we consult expert AI and databases</p>
              <p>üåæ The right crop can change your season!</p>
            </div>
          </div>
        </div>
      )}
      {error && <div className="mt-3 text-red-400 bg-background-light rounded p-2 border border-red-600 animate-pulse">{error}</div>}
      {parsedTable && (
        <div className="mt-6 overflow-x-auto">
          <table className="min-w-full border-collapse bg-background-light rounded-xl shadow border border-background">
            <thead>
              <tr>
                {parsedTable.header.map((h, i) => (
                  <th key={i} className="px-4 py-2 bg-emerald-700 text-white text-lg">{h}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {parsedTable.rows.map((row, i) => (
                <tr key={i} className={i % 2 === 0 ? '' : 'bg-emerald-50'}>
                  {row.map((cell, j) => (
                    <td key={j} className="px-4 py-2 border-b border-background text-gray-900">{cell}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
export default CropRecommendationForm;
