import React, { useState, useEffect } from 'react';
import { Link, NavLink } from 'react-router-dom';

const menu = [
  { name: 'Home', path: '/' },
  { name: 'Crop Recommendation', path: '/crop-recommendation' },
  { name: 'Weather', path: '/weather' },
  { name: 'Organic Tips', path: '/tips' },
  { name: 'Government Schemes', path: '/schemes' },
  { name: 'Dashboard', path: '/dashboard' },
  { name: 'Chatbot', path: '/chatbot' },
  { name: 'About', path: '/about' }, // Added About
];

function Navbar() {
  const [open, setOpen] = useState(false);
  const [dark, setDark] = useState(() => {
    if (typeof window !== 'undefined') {
      return localStorage.getItem('theme') === 'dark' || window.matchMedia('(prefers-color-scheme: dark)').matches;
    }
    return false;
  });

  useEffect(() => {
    if (dark) {
      document.documentElement.classList.add('dark');
      localStorage.setItem('theme', 'dark');
    } else {
      document.documentElement.classList.remove('dark');
      localStorage.setItem('theme', 'light');
    }
  }, [dark]);

  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-background/95 shadow-md backdrop-blur border-b border-primary-dark">
      <div className="max-w-6xl mx-auto px-4 flex items-center justify-between h-16">
        <Link to="/" className="flex items-center text-2xl font-bold text-primary gap-2">
          <span role="img" aria-label="logo">ðŸŒ¾</span> AgriGuru
        </Link>
        <button className="md:hidden p-2" onClick={() => setOpen(!open)}>
          <svg className="w-7 h-7 text-primary" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={open ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'} />
          </svg>
        </button>
        <button
          className="ml-4 p-2 rounded-full bg-card hover:bg-accent transition text-accent hover:text-background"
          onClick={() => setDark(d => !d)}
          aria-label="Toggle dark mode"
        >
          {dark ? (
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m8.66-13.66l-.71.71M4.05 19.07l-.71.71M21 12h-1M4 12H3m16.66 5.66l-.71-.71M4.05 4.93l-.71-.71M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
            </svg>
          ) : (
            <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 12.79A9 9 0 1111.21 3a7 7 0 009.79 9.79z" />
            </svg>
          )}
        </button>
        <div className={`md:flex gap-6 font-medium ${open ? 'block absolute bg-background left-0 right-0 top-16 shadow-lg p-4' : 'hidden md:block'}`}>
          {menu.map(item => (
            <NavLink key={item.path} to={item.path} onClick={() => setOpen(false)} className={({ isActive }) => isActive ? 'text-accent border-b-2 border-accent pb-1' : 'hover:text-primary transition'}>
              {item.name}
            </NavLink>
          ))}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
