import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';

const menu = [
  { name: 'Home', path: '/' },
  { name: 'Crop Recommendation', path: '/crop-recommendation' },
  { name: 'Weather', path: '/weather' },
  { name: 'Organic Tips', path: '/tips' },
  { name: 'Government Schemes', path: '/schemes' },
  { name: 'Crop Prices', path: '/crop-prices' },
  { name: 'Chatbot', path: '/chatbot' },
  { name: 'Satellite Insight', path: '/satellite-insight' },
];

function Navbar() {
  const [open, setOpen] = useState(false);
  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-darkBg border-b border-border shadow-card rounded-b-2xl transition-colors duration-300">
      <div className="max-w-6xl mx-auto px-4 flex items-center justify-between h-20">
        <Link to="/" className="flex items-center text-3xl font-heading font-bold text-emerald gap-2">
          <span role="img" aria-label="logo">ðŸŒ¿</span> <span className="tracking-tight">AgriGuru</span>
        </Link>
        <div className="flex items-center gap-2">
          <button className="md:hidden p-2 rounded-lg hover:bg-moss/20 transition-colors duration-200" onClick={() => setOpen(!open)} aria-label="Toggle menu">
            <svg className="w-7 h-7 text-emerald" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={open ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'} />
            </svg>
          </button>
        </div>
        <div className={`md:flex gap-2 font-sans font-medium items-center ${open ? 'block absolute bg-darkBg left-0 right-0 top-20 shadow-lg p-4 rounded-b-xl z-40' : 'hidden md:flex'}`}> 
          {menu.map(item => (
            <NavLink
              key={item.path}
              to={item.path}
              onClick={() => setOpen(false)}
              className={({ isActive }) =>
                `px-5 py-2 mx-1 rounded-xl transition-all duration-200 text-base flex items-center justify-center font-sans tracking-wide
                ${isActive ? 'bg-emerald text-darkBg font-bold shadow-card' : 'hover:bg-moss/30 hover:text-emerald text-text'} focus:outline-none focus:ring-2 focus:ring-emerald`
              }
            >
              {item.name}
            </NavLink>
          ))}
          {/* Unique Team icon button at the bottom */}
          <NavLink
            to="/about"
            aria-label="Team"
            className={({ isActive }) =>
              `fixed bottom-6 right-6 md:static md:ml-4 flex items-center justify-center rounded-full w-14 h-14 text-3xl shadow-card bg-emerald text-darkBg hover:bg-moss transition-all duration-200 z-50 border-4 border-darkBg ${isActive ? 'ring-4 ring-moss' : ''}`
            }
          >
            <span role="img" aria-label="team">ðŸ‘¥</span>
          </NavLink>
        </div>
      </div>
      <div className="divider" />
    </nav>
  );
}

export default Navbar;
