import React, { useEffect, useState } from 'react';

function SchemesExplorer() {
  const [state, setState] = useState('All');
  const allSchemes = [
    { name: 'PM-KISAN', summary: 'Direct income support to farmers.', state: 'All', url: '#' },
    { name: 'Soil Health Card', summary: 'Provides soil health cards to farmers.', state: 'All', url: '#' },
    { name: 'KALIA', summary: 'Comprehensive assistance for cultivators in Odisha.', state: 'Odisha', url: '#' },
    { name: 'Rythu Bandhu', summary: 'Investment support for farmers in Telangana.', state: 'Telangana', url: '#' },
    { name: 'Mukhya Mantri Krishi Ashirwad', summary: 'Direct benefit transfer scheme for Jharkhand farmers.', state: 'Jharkhand', url: '#' },
    { name: 'Krishi Yantra Subsidy', summary: 'Subsidy on farm machinery in Maharashtra.', state: 'Maharashtra', url: '#' }
  ];
  const states = ['All', 'Odisha', 'Telangana', 'Jharkhand', 'Maharashtra'];
  const schemes = state === 'All' ? allSchemes : allSchemes.filter(s => s.state === state || s.state === 'All');

  return (
    <div className="bg-white/80 p-6 rounded-2xl shadow-lg border border-green-200">
      <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4 mb-4">
        <h2 className="text-2xl font-bold text-green-800 flex items-center gap-2">
          <span role="img" aria-label="schemes">üèõÔ∏è</span> Active Farmer Schemes
        </h2>
        <div>
          <label className="mr-2 font-medium text-green-700">Search by State:</label>
          <select className="border border-green-300 rounded-lg p-2 focus:ring-2 focus:ring-green-400 outline-none transition" value={state} onChange={e => setState(e.target.value)}>
            {states.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>
      </div>
      <div className="grid gap-6 md:grid-cols-2">
        {schemes.map((scheme, i) => (
          <div key={i} className="bg-green-50 border border-green-100 rounded-xl p-4 shadow hover:shadow-lg transition group">
            <div className="flex items-center gap-2 mb-2">
              <span className="text-2xl">üå±</span>
              <span className="text-lg font-semibold text-green-900">{scheme.name}</span>
              <span className="ml-auto text-xs bg-green-200 text-green-800 rounded px-2 py-0.5">{scheme.state}</span>
            </div>
            <div className="text-green-800 mb-2">{scheme.summary}</div>
            <a href={scheme.url} target="_blank" rel="noopener noreferrer" className="inline-block mt-1 text-green-700 font-medium hover:underline group-hover:text-green-900 transition">Apply / Learn More ‚Üí</a>
          </div>
        ))}
      </div>
    </div>
  );
}
export default SchemesExplorer;
