import React, { useState, useRef, useEffect } from 'react';
import { askChatbot } from '../api/api';

function BotTyping() {
  return (
    <div className="flex items-center gap-2 text-leaf font-semibold py-2">
      <svg className="animate-spin h-5 w-5 text-leaf" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path></svg>
      <span>Thinking...</span>
    </div>
  );
}

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [language, setLanguage] = useState('en');
  const chatRef = useRef(null);

  useEffect(() => {
    if (chatRef.current) {
      chatRef.current.scrollTop = chatRef.current.scrollHeight;
    }
  }, [messages, loading]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    setMessages(msgs => [...msgs, { from: 'user', text: input }]);
    setInput(''); // Clear input immediately
    setLoading(true); setError(null);
    try {
      const res = await askChatbot({ message: input, language });
      setMessages(msgs => [...msgs, { from: 'bot', text: res.data.answer }]);
    } catch (err) {
      setError('Chatbot error');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-offwhite p-8 rounded-2xl shadow-card border border-hemlock">
      <h2 className="text-2xl font-heading font-bold text-olive mb-2">AgriGuru Chatbot</h2>
      <div className="mb-2 flex gap-2 items-center">
        <label htmlFor="chatbot-language" className="font-medium text-leaf">Language:</label>
        <select
          id="chatbot-language"
          className="border border-hemlock bg-offwhite text-black rounded-lg p-1 focus:ring-2 focus:ring-leaf outline-none transition"
          value={language}
          onChange={e => setLanguage(e.target.value)}
        >
          <option value="en">English</option>
          <option value="hi">Hindi</option>
          <option value="kn">Kannada</option>
        </select>
      </div>
      <div className="h-64 overflow-y-auto border border-hemlock p-2 mb-2 bg-gradient-to-br from-sky via-gold to-softgreen animate-gradient-move rounded" ref={chatRef}>
        {messages.map((msg, i) => (
          <div key={i} className={msg.from === 'user' ? 'flex justify-end' : 'flex justify-start'}>
            <span className={`${msg.from === 'user' ? (i % 2 === 0 ? 'feature-gold' : 'feature-green') : (i % 2 === 0 ? 'feature-green' : 'feature-gold')} max-w-[80%] text-base md:text-sm font-normal px-3 py-1.5 rounded-lg font-sans shadow inline-block my-0.5 text-black text-left break-words`}>{msg.text}</span>
          </div>
        ))}
        {loading && <BotTyping />}
      </div>
      {error && <div className="text-red-500 bg-offwhite rounded p-2 border border-red-600 animate-pulse mb-2">{error}</div>}
      <form onSubmit={handleSend} className="flex gap-2">
        <input className="border border-hemlock bg-offwhite text-black rounded-lg p-2 flex-1 focus:ring-2 focus:ring-leaf outline-none transition" value={input} onChange={e => setInput(e.target.value)} placeholder={`Ask a farming question (${language === 'en' ? 'English' : language === 'hi' ? 'Hindi' : 'Kannada'})`} />
        <button className="btn btn-green font-heading rounded-xl shadow-card transition-all duration-200 hover:scale-105 active:scale-100" type="submit" disabled={loading}>Send</button>
      </form>
    </div>
  );
}
export default Chatbot;
