import React, { useState } from 'react';
import { getSatelliteInsight } from '../api/api';

function SatelliteInsightPage() {
  const [location, setLocation] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFetch = async (e) => {
    e.preventDefault();
    setLoading(true); setError(null); setResult(null);
    try {
      const res = await getSatelliteInsight({ location });
      setResult(res.data);
    } catch (err) {
      setError('Failed to fetch satellite insight');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="max-w-xl mx-auto py-10 px-4 bg-card rounded-xl shadow-lg text-gray-100">
      <h2 className="text-3xl font-bold text-accent mb-6 flex items-center gap-2">
        <span role="img" aria-label="satellite">üõ∞Ô∏è</span> Satellite Insight
      </h2>
      <form onSubmit={handleFetch} className="flex flex-col sm:flex-row gap-3 mb-6">
        <input
          className="border border-background-light bg-background text-gray-100 rounded-lg p-2 flex-1 focus:ring-2 focus:ring-accent outline-none transition"
          placeholder="Enter location (city, village, etc.)"
          value={location}
          onChange={e => setLocation(e.target.value)}
          required
        />
        <button className="bg-accent hover:bg-primary text-background font-semibold p-2 rounded-lg shadow transition-all duration-200" type="submit" disabled={loading}>{loading ? 'Loading...' : 'Get Insight'}</button>
      </form>
      {error && <div className="text-red-400 bg-background-light rounded p-2 border border-red-600 animate-pulse mb-2">{error}</div>}
      {result && (
        <div className="bg-background-light rounded-xl shadow p-6 border border-background flex flex-col items-center">
          <div className="text-lg font-bold text-accent mb-2">{result.location}</div>
          <div className="flex gap-4 mb-4">
            <div className="flex flex-col items-center">
              <span className="text-2xl">üìç</span>
              <span className="text-gray-300 text-xs">Lat: {result.lat.toFixed(3)}</span>
              <span className="text-gray-300 text-xs">Lon: {result.lon.toFixed(3)}</span>
            </div>
            <div className="flex flex-col items-center">
              <span className="text-2xl">üå±</span>
              <span className="text-gray-300 text-xs">NDVI</span>
              <span className={`font-bold text-lg ${result.color === 'green' ? 'text-green-400' : result.color === 'yellow' ? 'text-yellow-300' : 'text-red-400'}`}>{result.ndvi}</span>
            </div>
            <div className="flex flex-col items-center">
              <span className="text-2xl">ü©∫</span>
              <span className="text-gray-300 text-xs">Status</span>
              <span className={`font-bold text-lg ${result.color === 'green' ? 'text-green-400' : result.color === 'yellow' ? 'text-yellow-300' : 'text-red-400'}`}>{result.status}</span>
            </div>
          </div>
          <div className="text-gray-100 text-center mb-2">{result.recommendation}</div>
        </div>
      )}
    </section>
  );
}

export default SatelliteInsightPage;
