import React from 'react';
import { useNavigate } from 'react-router-dom';

function Home() {
  const navigate = useNavigate();
  return (
    <section className="relative min-h-[80vh] flex flex-col items-center justify-center text-center py-16 bg-background text-gray-100">
      <div className="absolute inset-0 w-full h-full bg-gradient-to-br from-background via-background-light to-card opacity-90 pointer-events-none" />
      <div className="relative z-10 max-w-3xl mx-auto">
        <h1 className="text-5xl md:text-7xl font-extrabold text-accent mb-6 drop-shadow-lg">AgriGuru</h1>
        <p className="text-xl md:text-2xl text-gray-300 mb-8 font-medium">
          Empowering Farmers with AI: Get personalized crop recommendations, weather forecasts, organic farming tips, government schemes, and moreâ€”all in one elegant platform.
        </p>
        <button onClick={() => navigate('/crop-recommendation')} className="bg-primary hover:bg-primary-dark text-white font-bold px-10 py-4 rounded-lg shadow-lg hover:scale-105 transition-all text-lg mb-8">Get Started</button>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mt-8">
          <FeatureCard title="Crop Recommendation" desc="AI-driven suggestions for the best crops based on your soil, season, and location." />
          <FeatureCard title="Weather Forecast" desc="Accurate, location-based 5-day weather forecasts to plan your farming activities." />
          <FeatureCard title="Organic Tips" desc="Expert tips for sustainable and organic farming at every stage of your crop cycle." />
          <FeatureCard title="Government Schemes" desc="Explore the latest government schemes and subsidies for farmers in your region." />
          <FeatureCard title="Dashboard" desc="Track your queries, recommendations, and farming activities in a personalized dashboard." />
          <FeatureCard title="AI Chatbot" desc="Get instant answers to your farming questions in English or Hindi, powered by Gemini AI." />
          <FeatureCard title="Crop Health" desc="Visualize your crop health with NDVI and traffic light indicators (coming soon)." />
        </div>
      </div>
    </section>
  );
}

function FeatureCard({ title, desc }) {
  return (
    <div className="bg-card rounded-xl shadow-md p-6 border border-background-light hover:border-accent transition">
      <h3 className="text-xl font-bold text-accent mb-2">{title}</h3>
      <p className="text-gray-300 text-base">{desc}</p>
    </div>
  );
}

export default Home;
