import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';

const menu = [
  { name: 'Home', path: '/', icon: <span className="text-lg">üè†</span> },
  { name: 'Crop Recommendation', path: '/crop-recommendation', icon: <span className="text-lg">üå±</span> },
  { name: 'Weather', path: '/weather', icon: <span className="text-lg">‚õÖ</span> },
  { name: 'Organic Tips', path: '/tips', icon: <span className="text-lg">üçÉ</span> },
  { name: 'Government Schemes', path: '/schemes', icon: <span className="text-lg">üèõÔ∏è</span> },
  { name: 'Dashboard', path: '/dashboard', icon: <span className="text-lg">üìä</span> },
  { name: 'Chatbot', path: '/chatbot', icon: <span className="text-lg">ü§ñ</span> }
];

function Navbar() {
  const [open, setOpen] = useState(false);
  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-white/90 shadow-md backdrop-blur border-b border-green-200">
      <div className="max-w-6xl mx-auto px-4 flex items-center justify-between h-16 relative">
  <Link to="/" className="flex items-center text-3xl font-extrabold gap-2 font-pacifico text-green-700 tracking-tight" style={{fontFamily: 'Pacifico, cursive'}}>
    <span role="img" aria-label="flower" className="text-3xl">üå∏</span>
    AgriGuru
  </Link>
  <button className="md:hidden p-2" onClick={() => setOpen(!open)}>
    <svg className="w-7 h-7 text-green-700" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={open ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'} />
    </svg>
  </button>
  <div className={`md:flex gap-6 font-medium ${open ? 'block absolute bg-white left-0 right-0 top-16 shadow-lg p-4' : 'hidden md:block'}`}>
    {menu.map(item => (
      <NavLink
        key={item.path}
        to={item.path}
        onClick={() => setOpen(false)}
        className={({ isActive }) =>
          (isActive
            ? (item.highlight ? 'text-white bg-gradient-to-r from-green-500 to-blue-500 shadow-lg border-b-2 border-blue-600' : 'text-green-700 border-b-2 border-green-600 pb-1')
            : (item.highlight ? 'text-white bg-gradient-to-r from-green-500 to-blue-500 shadow-lg' : 'hover:text-green-600 transition')) +
          ' flex items-center gap-1 px-2 py-1 rounded-lg font-semibold ' + (item.highlight ? 'mx-1 px-3 py-1 text-base shadow-lg' : '')
        }
        style={item.highlight ? {fontWeight:'bold', letterSpacing:'0.5px'} : {}}
      >
        <span className="mr-1">{item.icon}</span>
        <span>{item.name}</span>
      </NavLink>
    ))}
  </div>
  {/* Satellite Insights Floating Button */}
  <Link
    to="/satellite"
    className="absolute right-2 md:right-0 top-1/2 -translate-y-1/2 z-50 group"
    title="Satellite Insights"
  >
    <span className="inline-flex items-center justify-center w-14 h-14 bg-gradient-to-br from-blue-500 to-green-400 text-white text-3xl rounded-full shadow-2xl border-4 border-white ring-4 ring-blue-200 group-hover:scale-110 transition-all animate-pulse-slow">
      üõ∞Ô∏è
    </span>
    <span className="absolute left-1/2 -translate-x-1/2 top-16 mt-1 bg-blue-700 text-white text-xs rounded px-2 py-1 shadow opacity-0 group-hover:opacity-100 transition pointer-events-none">Satellite Insights</span>
  </Link>
</div>
    </nav>
  );
}

export default Navbar;
