import React, { useState } from 'react';
import { askChatbot } from '../api/api';

function BotTyping() {
  return (
    <div className="flex items-center gap-2 text-accent font-semibold py-2">
      <span>Thinking</span>
      <span className="inline-block w-2 h-2 rounded-full bg-accent animate-bounce [animation-delay:0s]"></span>
      <span className="inline-block w-2 h-2 rounded-full bg-accent animate-bounce [animation-delay:0.2s]"></span>
      <span className="inline-block w-2 h-2 rounded-full bg-accent animate-bounce [animation-delay:0.4s]"></span>
    </div>
  );
}

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [language, setLanguage] = useState('en');
  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    setMessages([...messages, { from: 'user', text: input }]);
    setLoading(true); setError(null);
    try {
      const res = await askChatbot({ message: input, language });
      setMessages(msgs => [...msgs, { from: 'bot', text: res.data.answer }]);
    } catch (err) {
      setError('Chatbot error');
    } finally {
      setLoading(false);
      setInput('');
    }
  };

  return (
    <div className="bg-card p-6 rounded-xl shadow-lg">
      <h2 className="text-xl font-semibold text-accent mb-2">AgriGuru Chatbot</h2>
      <div className="mb-3 flex gap-3 items-center">
        <label htmlFor="chatbot-lang" className="text-gray-300 text-sm">Language:</label>
        <select id="chatbot-lang" value={language} onChange={e => setLanguage(e.target.value)} className="bg-background-light text-gray-100 border border-background rounded px-2 py-1 text-sm focus:outline-none">
          <option value="en">English</option>
          <option value="hi">Hindi</option>
          <option value="kn">Kannada</option>
        </select>
      </div>
      <div className="h-40 overflow-y-auto border border-background-light p-2 mb-2 bg-background-light rounded">
        {messages.map((msg, i) => (
          <div key={i} className={msg.from === 'user' ? 'text-right' : 'text-left'}>
            <span className={msg.from === 'user' ? 'bg-accent text-background' : 'bg-background text-gray-100'} style={{ padding: '6px 14px', borderRadius: '12px', display: 'inline-block', margin: '4px 0', fontWeight: 500 }}>{msg.text}</span>
          </div>
        ))}
      </div>
      {loading && <BotTyping />}
      {error && <div className="text-red-400 bg-background-light rounded p-2 border border-red-600 animate-pulse">{error}</div>}
      <form onSubmit={handleSend} className="flex gap-2">
        <input className="border border-background-light bg-background text-gray-100 rounded-lg p-2 flex-1 focus:ring-2 focus:ring-accent outline-none transition" value={input} onChange={e => setInput(e.target.value)} placeholder="Ask a question (English, Hindi, Kannada supported)" />
        <button className="bg-accent hover:bg-primary text-background font-semibold p-2 rounded-lg shadow transition-all duration-200" type="submit">Send</button>
      </form>
    </div>
  );
}
export default Chatbot;
