import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

// Accent color classes
const accentHeading = "text-green-700";
const accentIcon = "text-green-500";
const accentHighlight = "bg-gradient-to-br from-green-100 via-green-50 to-emerald-50";

function Home() {
  const navigate = useNavigate();
  const [showTeam, setShowTeam] = useState(false);
  return (
    <section className="relative min-h-[80vh] flex flex-col items-center justify-center text-center bg-darkBg text-text responsive-section">
      <div className="relative z-10 max-w-4xl mx-auto flex flex-col items-center">
        <img
          src="https://images.unsplash.com/photo-1501004318641-b39e6451bec6?auto=format&fit=crop&w=600&q=80"
          alt="Plant inspiration"
          className="w-36 h-36 object-cover rounded-full shadow-card border-4 border-emerald mb-8 bg-darkBg"
        />
        <h1>AgriGuru</h1>
        <div className="card mb-12 max-w-3xl w-full text-left flex flex-col gap-10 border border-border">
          {/* About Section */}
          <div>
            <h2 className="text-3xl font-heading font-bold text-emerald mb-2">About AgriGuru</h2>
            <div className="space-y-2">
              <div className="font-bold text-lg text-emerald">Empowering Indian Agriculture</div>
              <div className="font-semibold text-base text-sage">AI-powered platform for personalized crop advice, weather, government schemes, organic tips, and satellite insights.</div>
            </div>
          </div>
          <div className="divider-secondary" />
          {/* Features Section */}
          <div>
            <h2 className="text-3xl font-heading font-bold text-indigo mb-2">Key Features</h2>
            <ul className="grid grid-cols-1 md:grid-cols-2 gap-x-8 gap-y-3">
              <li className="font-bold text-indigo flex items-center gap-2">üå± <span className="text-text">Crop Recommendation</span></li>
              <li className="font-bold text-indigo flex items-center gap-2">‚òÄÔ∏è <span className="text-text">Weather Forecast</span></li>
              <li className="font-bold text-indigo flex items-center gap-2">üßë‚Äçüåæ <span className="text-text">Organic Tips</span></li>
              <li className="font-bold text-moss flex items-center gap-2">üèõÔ∏è <span className="text-text">Government Schemes</span></li>
