function FeatureCard({ title, desc, accent }) {
  const accentMap = {
    indigo: 'from-darkBg via-darkBg to-indigo/10 border-indigo',
    moss: 'from-darkBg via-darkBg to-moss/10 border-moss',
    default: 'from-darkBg via-darkBg to-indigo/10 border-indigo',
  };
  const cardClass = `bg-gradient-to-br rounded-2xl shadow-card p-6 border hover:border-indigo transition-all duration-200 flex flex-col gap-2 group cursor-pointer hover:scale-[1.03] active:scale-100` + ' ' + (accentMap[accent] || accentMap.default);
  const titleClass = 'text-indigo font-heading group-hover:text-emerald transition-colors duration-200';
  return (
    <div className={cardClass} tabIndex={0} aria-label={title}>
      <h3 className={`text-xl font-bold mb-1 ${titleClass}`}>{title}</h3>
      <p className="text-text text-base leading-relaxed font-sans opacity-90 group-hover:opacity-100 transition-opacity duration-200">{desc}</p>
    </div>
  );
}

function TeamMember({ name, role, part }) {
  return (
    <div className="flex flex-col items-center bg-darkBg rounded-xl p-4 shadow-soft w-full border border-indigo">
      <div className="text-lg font-bold text-indigo mb-1">{name}</div>
      <div className="text-sage text-sm font-medium text-center mb-1">{role}</div>
      <div className="text-xs text-indigo text-center">{part}</div>
    </div>
  );
}

