import React, { useState } from 'react';
import { askChatbot } from '../api/api';

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    setMessages([...messages, { from: 'user', text: input }]);
    setLoading(true); setError(null);
    try {
      const res = await askChatbot({ message: input, language: 'hi' });
      setMessages(msgs => [...msgs, { from: 'bot', text: res.data.answer }]);
    } catch (err) {
      setError('Chatbot error');
    } finally {
      setLoading(false);
      setInput('');
    }
  };

  return (
  <div className="bg-gradient-to-br from-green-50 via-emerald-100 to-green-100 rounded-2xl shadow-lg p-6 border border-green-200 max-w-2xl mx-auto">
    <div className="flex items-center gap-3 mb-3">
      <span className="inline-flex items-center justify-center bg-green-600 text-white rounded-full w-10 h-10 shadow-md text-2xl">🤖</span>
      <h2 className="text-2xl font-bold text-green-800 tracking-tight">AgriGuru Chatbot</h2>
    </div>
    <div className="h-72 overflow-y-auto bg-white/80 border border-green-100 rounded-xl p-4 mb-3 flex flex-col gap-2 custom-scrollbar">
      {messages.length === 0 && (
        <div className="text-green-500 text-center opacity-60 mt-16">Start the conversation! 🌱</div>
      )}
      {messages.map((msg, i) => (
        <div key={i} className={`flex ${msg.from === 'user' ? 'justify-end' : 'justify-start'} items-end w-full`}>
          {msg.from === 'bot' && <span className="mr-2 text-2xl">🤖</span>}
          <span
            className={`px-4 py-2 rounded-2xl shadow-sm max-w-xs whitespace-pre-line break-words text-base animate-fade-in ${msg.from === 'user' ? 'bg-emerald-200 text-green-900 rounded-br-lg' : 'bg-green-100 text-green-800 rounded-bl-lg'}`}
            style={{ animationDelay: `${i * 100}ms` }}
          >{msg.text}</span>
          {msg.from === 'user' && <span className="ml-2 text-2xl">🧑‍🌾</span>}
        </div>
      ))}
      {loading && (
        <div className="flex justify-start items-end w-full animate-pulse">
          <span className="mr-2 text-2xl">🤖</span>
          <span className="px-4 py-2 rounded-2xl bg-green-100 text-green-800 shadow-sm max-w-xs">Thinking...</span>
        </div>
      )}
    </div>
    {error && <div className="text-red-600 bg-red-50 rounded p-2 border border-red-200 animate-pulse mb-2">{error}</div>}
    <form onSubmit={handleSend} className="flex gap-2 mt-2">
      <input
        className="border border-green-300 rounded-lg p-2 flex-1 focus:ring-2 focus:ring-green-400 outline-none transition bg-white"
        value={input}
        onChange={e => setInput(e.target.value)}
        placeholder="Ask anything about farming, crops, weather... (Hindi supported)"
      />
      <button
        className="bg-gradient-to-r from-green-600 to-emerald-500 text-white font-semibold p-2 rounded-lg shadow hover:from-green-700 hover:to-emerald-600 transition-all duration-200"
        type="submit"
        disabled={loading || !input.trim()}
      >Send</button>
    </form>
  </div>
);
}
export default Chatbot;
