import React, { useEffect, useState } from 'react';
import { getTips } from '../api/api';

function TipsSection() {
  const [tips, setTips] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [query, setQuery] = useState("");
  const [answer, setAnswer] = useState(null);
  const [answerLoading, setAnswerLoading] = useState(false);

  useEffect(() => {
    setLoading(true); setError(null);
    getTips().then(res => setTips(res.data)).catch(() => setError('Failed to fetch tips')).finally(() => setLoading(false));
  }, []);

  const handleAsk = async (e) => {
    e.preventDefault();
    setAnswer(null); setAnswerLoading(true);
    // Simulate AI answer (replace with real API call if available)
    setTimeout(() => {
      setAnswer(`This is a helpful answer about: "${query}". (Replace with real AI response)`);
      setAnswerLoading(false);
    }, 1200);
  };

  return (
    <div className="bg-card rounded-2xl shadow-lg p-6 border border-background-light">
      <h2 className="text-2xl font-bold text-accent mb-3 flex items-center gap-2">
        <span role="img" aria-label="tips">ðŸŒ±</span> Organic Farming Tips
      </h2>
      {loading && <div className="text-accent">Loading...</div>}
      {error && <div className="text-red-400 bg-background-light rounded p-2 border border-red-600 animate-pulse">{error}</div>}
      <ul className="list-disc ml-6 mb-6 text-gray-100">
        {tips.map((tip, i) => (
          <li key={i} className="mb-1"><b className="text-accent">{tip.category} ({tip.stage}):</b> {tip.tip}</li>
        ))}
      </ul>
      <form onSubmit={handleAsk} className="flex flex-col sm:flex-row gap-2 items-stretch mb-2">
        <input
          className="border border-background-light bg-background text-gray-100 rounded-lg p-2 flex-1 focus:ring-2 focus:ring-accent outline-none transition"
          placeholder="Ask a question about organic farming, crops, or soil..."
          value={query}
          onChange={e => setQuery(e.target.value)}
          required
        />
        <button
          className="bg-accent hover:bg-primary text-background font-semibold p-2 rounded-lg shadow transition-all duration-200"
          type="submit"
          disabled={answerLoading || !query.trim()}
        >{answerLoading ? 'Thinking...' : 'Ask'}</button>
      </form>
      {answer && (
        <div className="mt-2 p-3 bg-background-light border border-background rounded-lg animate-fade-in text-accent">
          <b>AI Answer:</b> {answer}
        </div>
      )}
    </div>
  );
}
export default TipsSection;
