import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';
import leafyAccent from '../assets/leafy-accent.svg';

const menu = [
  { name: 'Home', path: '/' },
  { name: 'Crop Recommendation', path: '/crop-recommendation' },
  { name: 'Weather', path: '/weather' },
  { name: 'Organic Tips', path: '/tips' },
  { name: 'Government Schemes', path: '/schemes' },
  { name: 'Crop Prices', path: '/crop-prices' },
  { name: 'Chatbot', path: '/chatbot' },
  { name: 'Satellite Insight', path: '/satellite-insight' },
];

function Navbar() {
  const [open, setOpen] = useState(false);
  return (
    <nav
      style={{ background: `url(${leafyAccent}) no-repeat left top`, backgroundSize: '120px 120px' }}
      className="bg-primary text-text-light border-b-4 border-yellow shadow-card rounded-b-2xl transition-colors duration-300"
    >
      <div className="max-w-6xl mx-auto px-4 flex items-center justify-between h-20">
        <Link to="/" className="flex items-center text-3xl font-heading font-bold text-accent-bright gap-2">
          <span role="img" aria-label="logo">ðŸŒ±</span> <span className="tracking-tight">AgriGuru</span>
        </Link>
        <div className="flex items-center gap-2">
          <button className="md:hidden p-2 rounded-lg hover:bg-accent-bright/10 transition-colors duration-200 text-text-light" onClick={() => setOpen(!open)} aria-label="Toggle menu">
            <svg className="w-7 h-7 text-yellow" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={open ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'} />
            </svg>
          </button>
        </div>
        <div className={`md:flex gap-2 font-sans font-medium items-center ${open ? 'block absolute bg-primary left-0 right-0 top-20 shadow-lg p-4 rounded-b-xl z-40' : 'hidden md:flex'}`}> 
          {menu.map(item => (
            <NavLink
              key={item.path}
              to={item.path}
              onClick={() => setOpen(false)}
              className={({ isActive }) =>
                `px-5 py-2 mx-1 rounded-xl transition-all duration-200 text-base flex items-center justify-center font-sans tracking-wide
                ${isActive ? 'bg-yellow text-primary font-bold shadow-card' : 'hover:bg-accent-bright/10 hover:text-yellow text-text-light'} focus:outline-none focus:ring-2 focus:ring-yellow`
              }
            >
              {item.name}
            </NavLink>
          ))}
        </div>
      </div>
      <div className="divider" />
    </nav>
  );
}

export default Navbar;
