import React from 'react';
import { useNavigate } from 'react-router-dom';
import AboutInspiration from './AboutInspiration';

function Home() {
  const navigate = useNavigate();
  return (
    <>
      <div className="min-h-[60vh] flex flex-col items-center justify-center text-center py-16 px-4 rounded-2xl shadow-lg mb-8 bg-gradient-to-br from-green-100 via-emerald-100 to-green-200 animate-gradient-move">
        <div className="relative z-10">
          <h1 className="text-4xl md:text-6xl font-extrabold text-green-900 mb-4 drop-shadow-lg">Welcome to AgriGuru</h1>
          <p className="text-lg md:text-2xl text-green-800 mb-6 font-medium">AI-powered assistant for farmers</p>
          <button onClick={() => navigate('/crop-recommendation')} className="bg-gradient-to-r from-green-600 to-emerald-500 text-white font-bold px-8 py-3 rounded-lg shadow-lg hover:scale-105 transition-all text-lg">Get Started</button>
        </div>
      </div>
      <div className="mt-12">
        <h2 className="text-2xl font-bold text-green-800 text-center mb-4">Learn More</h2>
        <AboutInspiration />
      </div>
    </>
  );
}

export default Home;
