import React, { useState } from 'react';
import { recommendCrop } from '../api/api';

const soilTypes = ['Sandy', 'Clay', 'Silty', 'Peaty', 'Chalky', 'Loamy'];
const seasons = ['Kharif', 'Rabi', 'Zaid'];

function CropRecommendationForm() {
  const [form, setForm] = useState({ soil: '', season: '', location: '' });
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true); setError(null); setResult(null);
    try {
      const res = await recommendCrop({ ...form, user_id: 'guest' });
      setResult(res.data);
    } catch (err) {
      setError('Failed to get recommendation');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gradient-to-br from-green-100 to-emerald-100 rounded-2xl shadow-lg p-6 transition-transform hover:scale-[1.02] border border-green-200">
      <div className="flex items-center gap-3 mb-3">
        <span className="inline-flex items-center justify-center bg-green-600 text-white rounded-full w-10 h-10 shadow-md">
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
            <path strokeLinecap="round" strokeLinejoin="round" d="M12 2.25v19.5m9.75-9.75H2.25" />
          </svg>
        </span>
        <h2 className="text-2xl font-bold text-green-800 tracking-tight">Crop Recommendation</h2>
      </div>
      <form onSubmit={handleSubmit} className="flex flex-col gap-3">
        <div className="flex gap-3">
          <select className="border border-green-300 rounded-lg p-2 flex-1 focus:ring-2 focus:ring-green-400 outline-none transition" required value={form.soil} onChange={e => setForm({ ...form, soil: e.target.value })}>
            <option value="">Soil Type</option>
            {soilTypes.map(type => <option key={type} value={type}>{type}</option>)}
          </select>
          <select className="border border-green-300 rounded-lg p-2 flex-1 focus:ring-2 focus:ring-green-400 outline-none transition" required value={form.season} onChange={e => setForm({ ...form, season: e.target.value })}>
            <option value="">Season</option>
            {seasons.map(season => <option key={season} value={season}>{season}</option>)}
          </select>
        </div>
        <input className="border border-green-300 rounded-lg p-2 focus:ring-2 focus:ring-green-400 outline-none transition" required placeholder="Enter Location" value={form.location} onChange={e => setForm({ ...form, location: e.target.value })} />
        <button className="bg-gradient-to-r from-green-600 to-emerald-500 text-white font-semibold p-2 rounded-lg shadow hover:from-green-700 hover:to-emerald-600 transition-all duration-200" type="submit" disabled={loading}>{loading ? 'Loading...' : 'Get Recommendation'}</button>
      </form>
      {error && <div className="mt-3 text-red-600 bg-red-50 rounded p-2 border border-red-200 animate-pulse">{error}</div>}
      {result && (
        <div className="mt-4 p-4 bg-green-50 border border-green-200 rounded-lg animate-fade-in">
          <div className="text-lg font-semibold text-green-800 mb-1"><b>Suggested Crops:</b> {Array.isArray(result.crops) ? result.crops.join(', ') : result.crops}</div>
          <div className="text-green-900"><b>Reason:</b> {result.reason}</div>
        </div>
      )}
    </div>
  );
}
export default CropRecommendationForm;
