import React, { useState } from 'react';
import { getSatelliteInsight } from '../api/api';

function NDVICircle({ value }) {
  // value: 0-1
  const percent = Math.round(value * 100);
  const stroke = 8;
  const radius = 32;
  const normalizedRadius = radius - stroke / 2;
  const circumference = normalizedRadius * 2 * Math.PI;
  const strokeDashoffset = circumference - (percent / 100) * circumference;
  return (
    <svg height={radius * 2} width={radius * 2} className="block mx-auto">
      <circle
        stroke="#334155"
        fill="none"
        strokeWidth={stroke}
        r={normalizedRadius}
        cx={radius}
        cy={radius}
      />
      <circle
        stroke="#22D3EE"
        fill="none"
        strokeWidth={stroke}
        strokeLinecap="round"
        strokeDasharray={circumference + ' ' + circumference}
        style={{ strokeDashoffset, transition: 'stroke-dashoffset 0.5s' }}
        r={normalizedRadius}
        cx={radius}
        cy={radius}
      />
      <text
        x="50%"
        y="50%"
        textAnchor="middle"
        dy=".3em"
        fontSize="1.1rem"
        fill="#F8FAFC"
        fontWeight="bold"
      >
        {value}
      </text>
    </svg>
  );
}

export default function SatelliteInsightPage() {
  const [location, setLocation] = useState('');
  const [lat, setLat] = useState('');
  const [lon, setLon] = useState('');
  const [useCoords, setUseCoords] = useState(false);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showInfo, setShowInfo] = useState(false);

  const handleFetch = async (e) => {
    e.preventDefault();
    setLoading(true); setError(null); setResult(null);
    try {
      let payload = {};
      if (useCoords && lat && lon) {
        payload = { lat: parseFloat(lat), lon: parseFloat(lon) };
      } else {
        payload = { location };
      }
      const res = await getSatelliteInsight(payload);
      setResult(res.data);
    } catch (err) {
      setError('Failed to fetch satellite insight');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="bg-darkBg text-text">
      <div className="w-full max-w-3xl mx-auto py-section mt-16">
        <h2 className="h2 flex items-center gap-2 mb-4">
          <span role="img" aria-label="satellite">üõ∞Ô∏è</span> Satellite Insight
        </h2>
        <div className="divider" />
        <form onSubmit={handleFetch} className="flex flex-col gap-3 mb-6">
          <div className="flex items-center gap-3 mb-2">
            <label className="text-sm text-sage font-medium">
              <input type="checkbox" checked={useCoords} onChange={e => setUseCoords(e.target.checked)} className="mr-2" />
              Use exact coordinates
            </label>
          </div>
          {useCoords ? (
            <div className="flex flex-col sm:flex-row gap-3">
              <input
                className="border border-border bg-darkBg text-text rounded-lg p-2 flex-1 focus:ring-2 focus:ring-emerald outline-none transition-colors duration-200"
                placeholder="Latitude (e.g. 29.335)"
                value={lat}
                onChange={e => setLat(e.target.value)}
                type="number"
                step="any"
                required
              />
              <input
                className="border border-border bg-darkBg text-text rounded-lg p-2 flex-1 focus:ring-2 focus:ring-emerald outline-none transition-colors duration-200"
                placeholder="Longitude (e.g. 80.099)"
                value={lon}
                onChange={e => setLon(e.target.value)}
                type="number"
                step="any"
                required
              />
            </div>
          ) : (
            <input
              className="border border-border bg-darkBg text-text rounded-lg p-2 flex-1 focus:ring-2 focus:ring-emerald outline-none transition-colors duration-200"
              placeholder="Enter location (city, village, etc.)"
              value={location}
              onChange={e => setLocation(e.target.value)}
              required
            />
          )}
          <button
            className="btn"
            type="submit"
            disabled={loading || (useCoords && (!lat || !lon)) || (!useCoords && !location)}
          >
            {loading ? 'Loading...' : 'Get Insight'}
          </button>
        </form>
        {error && <div className="text-red-400 bg-card rounded p-2 border border-red-600 animate-pulse mb-2">{error}</div>}
        {result && (
          <div className="bg-card rounded-2xl shadow-card p-6 border border-background flex flex-col items-center w-full">
            <div className="w-full flex flex-col sm:flex-row justify-between items-center mb-4">
              <div className="flex flex-col items-center mb-2 sm:mb-0">
                <span className="text-2xl">üìç</span>
                <span className="text-text/80 text-sm font-semibold">Location</span>
                <span className="text-accent font-bold text-lg">{result.location}</span>
                <span className="text-text/60 text-xs">Lat: {result.lat.toFixed(3)} | Lon: {result.lon.toFixed(3)}</span>
              </div>
              <div className="flex flex-col items-center">
                <span className="text-2xl">üå±</span>
                <span className="text-text/80 text-sm font-semibold">NDVI</span>
                <div className="relative flex flex-col items-center">
                  <NDVICircle value={result.ndvi} />
                  <span className="absolute top-0 right-0 px-2 py-0.5 rounded-full text-xs font-bold bg-accent text-background">{result.status}</span>
                </div>
              </div>
            </div>
            <div className="w-full flex flex-col items-center mb-4">
              <span className="text-2xl">üßë‚Äçüåæ</span>
              <span className="text-text/80 text-sm font-semibold">Crop Health</span>
              <span className="font-bold text-lg text-accent">{result.status}</span>
              <div className="text-text text-center mt-1">{result.recommendation}</div>
            </div>
            {/* Satellite image preview */}
            {result.lat && result.lon && (
              <div className="w-full flex flex-col items-center mb-4">
                <span className="text-text/80 text-sm font-semibold mb-1">Satellite Image Preview</span>
                <img
                  src={`https://static-maps.yandex.ru/1.x/?lang=en-US&ll=${result.lon},${result.lat}&z=10&l=sat&size=450,250`}
                  alt="Satellite preview"
                  className="rounded-lg shadow border border-background max-w-xs"
                  style={{ minHeight: 120, background: '#222' }}
                  onError={e => { e.target.style.display = 'none'; }}
                />
                <span className="text-xs text-text/60 mt-1">Powered by Yandex Satellite</span>
              </div>
            )}
            <button
              className="mt-2 text-xs text-accent underline hover:text-background"
              onClick={() => setShowInfo(v => !v)}
              type="button"
            >
              {showInfo ? 'Hide' : 'What is NDVI & Crop Health?'}
            </button>
            {showInfo && (
              <div className="mt-2 p-3 bg-darkBg border-l-4 border-emerald rounded shadow text-text text-sm w-full">
                <b>NDVI (Normalized Difference Vegetation Index):</b> NDVI is a satellite-derived value between 0 and 1 that measures crop greenness and health. Higher values (closer to 1) mean healthier, greener crops. <br />
                <b>Crop Health:</b> Based on NDVI, this shows if crops are healthy, moderate, or stressed. <br />
                <span className="text-xs text-sage">üü¢ Healthy: NDVI &gt; 0.7 &nbsp; | &nbsp; üü° Moderate: 0.5 - 0.7 &nbsp; | &nbsp; üü§ Unhealthy: &lt; 0.5</span>
              </div>
            )}
          </div>
        )}
      </div>
    </section>
  );
}
