import React, { useState } from 'react';
import { Link, NavLink } from 'react-router-dom';

const menu = [
  { name: 'Home', path: '/', icon: 'üè°', desc: 'Start' },
  { name: 'Crop Recommendation', path: '/crop-recommendation', icon: 'üå±', desc: 'Best Crops' },
  { name: 'Weather', path: '/weather', icon: '‚òÄÔ∏è', desc: 'Forecast' },
  { name: 'Organic Tips', path: '/tips', icon: 'üçÉ', desc: 'Farming Advice' },
  { name: 'Government Schemes', path: '/schemes', icon: 'üèõÔ∏è', desc: 'Schemes' },
  { name: 'Dashboard', path: '/dashboard', icon: 'üìä', desc: 'Your Data' },
  { name: 'Chatbot', path: '/chatbot', icon: 'ü§ñ', desc: 'Ask AI' }
];

function Navbar() {
  const [open, setOpen] = useState(false);
  return (
    <nav className="fixed top-0 left-0 right-0 z-50 bg-white/90 shadow-md backdrop-blur border-b border-green-200">
      <div className="max-w-6xl mx-auto px-4 flex items-center justify-between h-16">
        <Link to="/" className="flex items-center text-2xl font-bold text-green-700 gap-2">
          <span role="img" aria-label="logo">üåæ</span> AgriGuru
        </Link>
        <button className="md:hidden p-2" onClick={() => setOpen(!open)}>
          <svg className="w-7 h-7 text-green-700" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d={open ? 'M6 18L18 6M6 6l12 12' : 'M4 6h16M4 12h16M4 18h16'} />
          </svg>
        </button>
        <div className={`md:grid md:grid-cols-7 gap-2 font-medium text-center ${open ? 'block absolute bg-white left-0 right-0 top-16 shadow-lg p-4' : 'hidden md:grid'}`} style={{ minWidth: 0 }}>
          {menu.map(item => (
            <NavLink
              key={item.path}
              to={item.path}
              onClick={() => setOpen(false)}
              className={({ isActive }) =>
                `flex flex-col items-center justify-center px-2 py-1 rounded-lg whitespace-nowrap overflow-hidden outline-none transition-all duration-200 ease-in-out
                ${isActive ? 'bg-green-100 text-green-700 font-semibold shadow border-b-2 border-green-600' :
                  'hover:bg-green-50 hover:text-green-700 focus-visible:ring-2 focus-visible:ring-green-400 focus-visible:ring-offset-2 hover:scale-105 hover:shadow-md'}
                `
              }
            >
              <span className="text-xl mb-1">{item.icon}</span>
              <span className="text-xs font-medium leading-tight">{item.desc}</span>
              <span className="text-[13px] font-normal hidden md:block">{item.name}</span>
            </NavLink>
          ))}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
