// AboutInspiration.jsx
// Modular component for About Us, Novelty, and Inspiration modals/sections
import React, { useState } from 'react';

const sections = [
  {
    key: 'about',
    label: 'About Us',
    content: (
      <div>
        <h3 className="text-xl font-bold mb-4 text-green-800 text-center">About Us</h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 mb-4">
          {/* Saurabh Joshi */}
          <div className="bg-green-50 p-4 rounded-xl flex items-center gap-4 shadow-sm">
            <span className="text-4xl">🧑‍💻</span>
            <div>
              <div className="font-semibold text-green-900">Saurabh Joshi</div>
              <div className="text-green-700 text-sm">Full-stack Developer</div>
            </div>
          </div>
          {/* Trisa Das */}
          <div className="bg-green-50 p-4 rounded-xl flex items-center gap-4 shadow-sm">
            <span className="text-4xl">👩‍🔬</span>
            <div>
              <div className="font-semibold text-green-900">Trisa Das</div>
              <div className="text-green-700 text-sm">Agri Data Scientist</div>
            </div>
          </div>
          {/* Nithaksha G */}
          <div className="bg-green-50 p-4 rounded-xl flex items-center gap-4 shadow-sm">
            <span className="text-4xl">🧑‍🎨</span>
            <div>
              <div className="font-semibold text-green-900">Nithaksha G</div>
              <div className="text-green-700 text-sm">UI/UX & Frontend</div>
            </div>
          </div>
          {/* Chiranthan M S */}
          <div className="bg-green-50 p-4 rounded-xl flex items-center gap-4 shadow-sm">
            <span className="text-4xl">🧑‍🔧</span>
            <div>
              <div className="font-semibold text-green-900">Chiranthan M S</div>
              <div className="text-green-700 text-sm">Backend & Integration</div>
            </div>
          </div>
        </div>
        <div className="mb-1"><b>Tech Used:</b> React, Flask, MongoDB, Gemini API, OpenWeather API, SentinelHub</div>
        <div className="mt-2 text-green-900">“AgriGuru is an AI-powered farming assistant designed to empower Indian farmers with smart crop recommendations, real-time weather updates, and government scheme information.”</div>
      </div>
    )
  },
  {
    key: 'novelty',
    label: 'Novelty of Idea',
    content: (
      <div>
        <h3 className="text-xl font-bold mb-2 text-green-800">Novelty of Idea</h3>
        <ul className="list-disc ml-6 text-green-900 mb-2">
          <li>Combines crop AI, satellite NDVI insights, weather & govt data</li>
          <li>Multilingual chatbot for accessibility</li>
          <li>User dashboard for personalization</li>
        </ul>
      </div>
    )
  },
  {
    key: 'inspiration',
    label: 'Inspiration',
    content: (
      <div>
        <h3 className="text-xl font-bold mb-2 text-green-800">Inspiration</h3>
        <div className="mb-1">“Inspired by the struggles of small and marginal farmers in India, especially those lacking access to reliable crop planning tools and weather data.”</div>
        <div className="mb-1">“Built for social impact — to bridge the gap between tech and agriculture.”</div>
      </div>
    )
  }
];

/**
 * Modular About/Inspiration/Novelty Section
 * - Center-aligned on desktop, stacked on mobile
 * - Each button opens a modal with content
 */
export default function AboutInspiration() {
  const [open, setOpen] = useState(null);

  return (
    <div className="flex flex-col items-center pt-8 space-y-4 w-full max-w-xl mx-auto">
      {/* Render buttons */}
      <div className="flex flex-col sm:flex-row gap-4 w-full justify-center">
        {sections.map(sec => (
          <button
            key={sec.key}
            className="bg-green-600 text-white font-semibold py-2 px-6 rounded-lg shadow hover:bg-green-700 transition-all w-full sm:w-auto"
            onClick={() => setOpen(sec.key)}
          >{sec.label}</button>
        ))}
      </div>
      {/* Render modal overlay if open */}
      {open && (
        <div
          className="fixed inset-0 z-40 flex items-center justify-center bg-black/30 backdrop-blur-sm"
          onClick={() => setOpen(null)}
        >
          <div
            className="bg-white rounded-2xl shadow-xl p-6 max-w-lg w-full mx-4 relative animate-fade-in"
            onClick={e => e.stopPropagation()}
          >
            <button
              className="absolute top-2 right-2 text-green-700 hover:text-green-900 text-xl font-bold"
              onClick={() => setOpen(null)}
              aria-label="Close"
            >&times;</button>
            {sections.find(s => s.key === open)?.content}
          </div>
        </div>
      )}
    </div>
  );
}
