import React, { useState, useRef, useEffect } from 'react';
import { askChatbot } from '../api/api';

function BotTyping() {
  return (
    <div className="flex items-center gap-2 text-accent font-semibold py-2">
      <svg className="animate-spin h-5 w-5 text-accent" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24"><circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle><path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path></svg>
      <span>Thinking...</span>
    </div>
  );
}

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const chatRef = useRef(null);

  useEffect(() => {
    if (chatRef.current) {
      chatRef.current.scrollTop = chatRef.current.scrollHeight;
    }
  }, [messages, loading]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;
    setMessages(msgs => [...msgs, { from: 'user', text: input }]);
    setLoading(true); setError(null);
    try {
      const res = await askChatbot({ message: input });
      setMessages(msgs => [...msgs, { from: 'bot', text: res.data.answer }]);
    } catch (err) {
      setError('Chatbot error');
    } finally {
      setLoading(false);
      setInput('');
    }
  };

  return (
    <div className="bg-card p-6 rounded-xl shadow-lg">
      <h2 className="text-xl font-semibold text-accent mb-2">AgriGuru Chatbot</h2>
      <div className="h-64 overflow-y-auto border border-background-light p-2 mb-2 bg-background-light rounded" ref={chatRef}>
        {messages.map((msg, i) => (
          <div key={i} className={msg.from === 'user' ? 'text-right' : 'text-left'}>
            <span className={msg.from === 'user' ? 'bg-accent text-background' : 'bg-background text-gray-100'} style={{ padding: '6px 14px', borderRadius: '12px', display: 'inline-block', margin: '4px 0', fontWeight: 500 }}>{msg.text}</span>
          </div>
        ))}
        {loading && <BotTyping />}
      </div>
      {error && <div className="text-red-400 bg-background-light rounded p-2 border border-red-600 animate-pulse">{error}</div>}
      <form onSubmit={handleSend} className="flex gap-2">
        <input className="border border-background-light bg-background text-gray-100 rounded-lg p-2 flex-1 focus:ring-2 focus:ring-accent outline-none transition" value={input} onChange={e => setInput(e.target.value)} placeholder="Ask a farming question (English only, fast)" />
        <button className="bg-accent hover:bg-primary text-background font-semibold p-2 rounded-lg shadow transition-all duration-200" type="submit" disabled={loading}>Send</button>
      </form>
    </div>
  );
}
export default Chatbot;
