import React, { useState } from 'react';
import { getSatelliteInsights } from '../api/api';

function SatelliteInsightsPage() {
  const [location, setLocation] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFetch = async () => {
    setLoading(true); setError(null); setResult(null);
    try {
      // This calls your backend, which should in turn call OpenWeather's Satellite API
      const res = await getSatelliteInsights({ location });
      setResult(res.data);
    } catch (err) {
      setError('Failed to fetch satellite insights');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gradient-to-br from-blue-200 to-green-100 rounded-2xl shadow-xl border-4 border-blue-400 p-8 max-w-3xl mx-auto mt-6 mb-8">
      <div className="flex items-center gap-3 mb-6">
        <span className="inline-flex items-center justify-center bg-blue-600 text-white rounded-full w-12 h-12 shadow-md text-3xl">üõ∞Ô∏è</span>
        <h2 className="text-3xl font-extrabold text-blue-900 tracking-tight">Satellite Insights</h2>
      </div>
      <div className="flex flex-col sm:flex-row gap-3 mb-4">
        <input className="border border-blue-300 rounded-lg p-3 flex-1 focus:ring-2 focus:ring-blue-400 outline-none transition text-lg" placeholder="Enter Location or Coordinates" value={location} onChange={e => setLocation(e.target.value)} />
        <button className="bg-gradient-to-r from-blue-600 to-green-500 text-white font-bold p-3 rounded-lg shadow hover:from-blue-700 hover:to-green-600 transition-all duration-200 text-lg" onClick={handleFetch} disabled={loading}>{loading ? 'Loading...' : 'Get Insights'}</button>
      </div>
      {error && <div className="text-red-600 bg-red-50 rounded p-2 border border-red-200 animate-pulse mb-2">{error}</div>}
      {result && (
        <div className="mt-4 p-4 bg-white/80 border border-blue-200 rounded-xl animate-fade-in text-blue-900">
          {/* Example output, adapt as needed for your real API response */}
          <div className="mb-2"><b>NDVI (Vegetation Index):</b> {result.ndvi}</div>
          <div className="mb-2"><b>Cloud Coverage:</b> {result.cloud_coverage}%</div>
          <div className="mb-2"><b>Soil Moisture:</b> {result.soil_moisture}</div>
          <div className="mb-2"><b>Satellite Image:</b></div>
          {result.image_url && <img src={result.image_url} alt="Satellite View" className="rounded-lg shadow border mb-2 max-h-80" />}
          <div className="text-sm text-blue-700">Data powered by OpenWeather Satellite API</div>
        </div>
      )}
    </div>
  );
}
export default SatelliteInsightsPage;
