import React from 'react';

const team = [
  { name: 'Saurabh Joshi', role: 'Project Lead & Backend Developer', email: 'saurabh.joshi@example.com' },
  { name: 'Trisa Das', role: 'Frontend Developer', email: 'trisa.das@example.com' },
  { name: 'Nithaksha G', role: 'UI/UX Designer', email: 'nithaksha.g@example.com' },
  { name: 'Chiranthan M S', role: 'AI/ML Specialist', email: 'chiranthan.ms@example.com' },
];

function AboutPage() {
  return (
    <section className="bg-background text-text">
      <div className="max-w-3xl mx-auto py-section px-4 mt-16">
        <h2 className="h1 mb-2">About AgriGuru</h2>
        <div className="divider" />
        <p className="text-lg text-text/80 mb-8 font-sans">
          AgriGuru is a passion project developed by a dedicated team of students and professionals, aiming to empower farmers with the latest AI technology. Our mission is to make advanced agricultural knowledge accessible, actionable, and easy to use for everyone.
        </p>
        <h3 className="h2 mb-4">Meet the Team</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {team.map(member => (
            <div key={member.email} className="bg-card rounded-2xl p-6 shadow-card border border-background flex flex-col gap-2 hover:border-accent transition-all duration-200">
              <h4 className="text-xl font-heading font-semibold text-accent mb-1">{member.name}</h4>
              <p className="text-text/80 mb-1 font-sans">{member.role}</p>
              <a href={`mailto:${member.email}`} className="text-accent hover:underline text-sm font-sans">{member.email}</a>
            </div>
          ))}
        </div>
        <div className="divider" />
        <div className="mt-8 text-text/60 text-sm text-center">
          &copy; {new Date().getFullYear()} AgriGuru Team. All rights reserved.
        </div>
      </div>
    </section>
  );
}

export default AboutPage;
