import React from 'react';

const team = [
  { name: 'Sonu Jha', role: 'Project Lead & Backend Developer', email: 'sonujha@example.com' },
  { name: 'Amit Kumar', role: 'Frontend Developer', email: 'amitkumar@example.com' },
  { name: 'Priya Singh', role: 'AI/ML Specialist', email: 'priyasingh@example.com' },
  { name: 'Ravi Patel', role: 'UI/UX Designer', email: 'ravipatel@example.com' },
];

function AboutPage() {
  return (
    <section className="max-w-3xl mx-auto py-12 px-4 bg-background rounded-xl shadow-lg mt-8 text-gray-100">
      <h2 className="text-4xl font-extrabold text-accent mb-4">About AgriGuru</h2>
      <p className="text-lg text-gray-300 mb-8">
        AgriGuru is a passion project developed by a dedicated team of students and professionals, aiming to empower farmers with the latest AI technology. Our mission is to make advanced agricultural knowledge accessible, actionable, and easy to use for everyone.
      </p>
      <h3 className="text-2xl font-bold text-primary mb-4">Meet the Team</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {team.map(member => (
          <div key={member.email} className="bg-card rounded-lg p-5 shadow border border-background-light">
            <h4 className="text-xl font-semibold text-accent mb-1">{member.name}</h4>
            <p className="text-gray-200 mb-1">{member.role}</p>
            <a href={`mailto:${member.email}`} className="text-primary hover:underline text-sm">{member.email}</a>
          </div>
        ))}
      </div>
      <div className="mt-8 text-gray-400 text-sm text-center">
        &copy; {new Date().getFullYear()} AgriGuru Team. All rights reserved.
      </div>
    </section>
  );
}

export default AboutPage;
