import React from 'react';
import { useNavigate } from 'react-router-dom';

function Home() {
  const navigate = useNavigate();
  return (
    <section className="relative min-h-[60vh] flex flex-col items-center justify-center text-center py-12">
      <img src="https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=1500&q=80" alt="Farming" className="absolute inset-0 w-full h-full object-cover opacity-30 pointer-events-none" />
      <div className="relative z-10">
        <h1 className="text-4xl md:text-6xl font-extrabold text-green-900 mb-4 drop-shadow-lg">Welcome to AgriGuru</h1>
        <p className="text-lg md:text-2xl text-green-800 mb-6 font-medium">AI-powered assistant for farmers</p>
        <button onClick={() => navigate('/crop-recommendation')} className="bg-gradient-to-r from-green-600 to-emerald-500 text-white font-bold px-8 py-3 rounded-lg shadow-lg hover:scale-105 transition-all text-lg">Get Started</button>
      </div>
    </section>
  );
}

export default Home;
